import { LeapActionType } from "../actiontype/leap.actiontype";
import CommonAction from "../actiontype/common.actiontype";
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";
import { ScenarioActionType } from "../actiontype/scenario.actiontype";

const initialState = {
  leapStartDate: null,
  leapEndDate: null,
  leapNetworkData: [],
  leapInfo: {
    cdrType: 'Data',
    daysGap: 0,
    leapCalendarStartLimit: 30,
    leapMaxDateRangeLimit: 15,
    leapMinDateRangeLimit: 7
  },
  leapDetails: {},
  verifiedLeapAddress: null,
  leapCalendarStartDate: null,
  leapTableRowChecked: {},
  disableLeapTableCheckbox: true,
  addressVerified: false,
  leapCaptureLoaction: null,
  leapLatLong: '',
  latitude: '',
  longitude: ''
};

