export default (state = initialState, action) => {
  switch (action.type) {
    case LeapActionType.SET_LEAP_START_DATE:
      return {
        ...state,
        leapStartDate: action.payload
      };
    case LeapActionType.SET_LEAP_END_DATE:
      return {
        ...state,
        leapEndDate: action.payload
      };
    case LeapActionType.UPADTE_LEAP_NETWORK_TABLE_DATE:
      return {
        ...state,
        leapNetworkData: action.payload
      };
    case LeapActionType.UPDATE_LEAP_INFO:
      return {
        ...state,
        leapInfo: action.payload
      };
    case LeapActionType.UPDATE_LEAP_DETAILS:
      return {
        ...state,
        leapDetails: action.payload
      };
    case LeapActionType.UPDATE_LEAP_VERIFIED_ADDRESS:
      return {
        ...state,
        verifiedLeapAddress: action.payload
      };
    case LeapActionType.SET_LEAP_CALENDAR_START_DATE:
      return {
        ...state,
        leapCalendarStartDate: action.payload
      };
    case LeapActionType.UPDATE_LEAP_TABLE_ROW_CHECKBOX:
      return {
        ...state,
        leapTableRowChecked: action.payload.leapTableRowChecked,
        disableLeapTableCheckbox: action.payload.disableLeapTableCheckbox,
        addressVerified: action.payload.addressVerified
      };
    case LeapActionType.UPDATE_LEAP_CAPTURE_LOCATION:
      return {
        ...state,
        leapCaptureLoaction: action.payload
      };
    case LeapActionType.UPDATE_LEAP_LAT_LONG:
      return {
        ...state,
        leapLatLong: action.payload
      };
    case LeapActionType.UPDATE_LEAP_LATITUDE:
      return {
        ...state,
        latitude: action.payload
      };
    case LeapActionType.UPDATE_LEAP_LONGITUDE:
      return {
        ...state,
        longitude: action.payload
      };
    case LeapActionType.UPDATE_LEAP_NETWORK_TABLE_SELECTED_ROW:
      return {
        ...state,
        leapTableRowChecked: action.payload
      };
    case CommonAction.RESET_STATE:
    case ScenarioActionType.RESET_SCENARIO_VALUES:
    case CustomerInfoActionType.RESET_CUSTOMER:
      return {
        ...state,
        ...initialState
      };
    default:
      return state;
  }
};
