import React, { Component } from 'react';
import renderHTML from 'react-render-html';
import ScenarioService from '../../../services/scenario.service';



class ScenarioDescription extends Component {


  componentDidUpdate() {
    this.handleDescLinks();
  }

  componentDidMount() {
    this.handleDescLinks();
  }

  handleDescLinks() {
    if (this.props.scenarioDescription && this.refs.scenarioDesc) {
      // Handle links which open new scenarios
      const scenarioLinks = this.refs.scenarioDesc.querySelectorAll('a[href*="/scenario/"]');
      const featureLinks = this.refs.scenarioDesc.querySelectorAll('a[href*="/feature/"]');
      if (scenarioLinks && scenarioLinks.length > 0) {
        for (let i = 0; i < scenarioLinks.length; i++) {
          scenarioLinks[i].onclick = (event) => {
            event.preventDefault();
            const scenarioId = ScenarioService.getScenarioIdFromScenarioDescLink(scenarioLinks[i].href);
            if (scenarioId) {
              this.props.openScenarioFromDescLink(scenarioId);
            }
          }
        }
      }
      else if (featureLinks && featureLinks.length > 0) {
        for (let i = 0; i < featureLinks.length; i++) {
          featureLinks[i].onclick = (event) => {
            event.preventDefault();
            const featureName = ScenarioService.getFeatureNameFromScenarioDescription(featureLinks[i].href);
            if (featureName) {
              this.props.handleFeatureLink(featureName)
            }
          }
        }
      }
    }
  }


  render() {
    return (
      <div ref="scenarioDesc" className={this.props.class}>
        {renderHTML(this.props.scenarioDescription)}
      </div>
    );

  }
}

export default ScenarioDescription;
