import Info from '@material-ui/icons/Info';
import BillComparatorCalendar from '../../../common/bill-comparator-calendar/bill-comparator-calendar.smart';
import Tooltips from '../../../common/tooltip/tooltip.dumb';
import { AppConstant } from '../../../../constants/app.constant';
import ArrowDropDown from '@material-ui/icons/ArrowDropDown';
import ArrowDropUp from '@material-ui/icons/ArrowDropUp';
import ArrowLeft from '@material-ui/icons/ArrowLeft';
import ArrowRight from '@material-ui/icons/ArrowRight';
import ExternalIcon from '../../../../assets/images/external.svg';
import UtilService from '../../../../services/util.service';

class BillComparatorDialog extends Component {
    state = {}
    tooltipPosition = "right";

    headers = {
        OUTSTANDING_BREAKUP: {
            label: 'OUTSTANDING BREAKUP',
            class: 'outstanding width-16'
        },
        USAGE_RENTAL: {
            label: 'USAGE / RENTAL',
            class: 'width-12'
        },
        DISCOUNT: {
            label: 'DISCOUNT',
            class: 'width-12'
        },
        NET_CHARGES: {
            label: 'NET CHARGES',
            class: 'net-charges-head width-12'
        },
        DIFFERENCES: {
            label: 'DIFFERENCES',
            class: 'difference-head width-12'
        },
    }


    componentDidMount() {
        this.setPanelExapnsionState();
    }

    componentDidUpdate() {
        if (UtilService.isPlatformWindows()) {
            if (document.getElementById('tableBodyScroll') && parseInt(document.getElementById('tableBodyScroll').scrollHeight) > 275) {
                document.getElementById('tableTotal').style.width = 'calc(100% - 8px)';
                document.getElementById('tableHeaderRow').style.width = 'calc(100% - 8px)';
                document.getElementById('calenderRow').style.width = 'calc(100% - 8px)';
            }
        }
    }

    getTotal() {
        const { classes } = this.props;
        if (this.props.data && this.props.data.billComparisonTotal) {
            const billComparisonTotal = this.props.data.billComparisonTotal;
            const previousBillCycle = this.props.data.previousBillCycle
            return (
                <Table className={classes.table} id="tableTotal">
                    <TableBody>
                        <TableRow className="row-head">
                            <TableCell className="bold fs-16 total-bill width-16" component="th" scope="row">
                                {billComparisonTotal.name}
                            </TableCell>
                            <TableCell colSpan={3} className={"bold width-36 fs-16 total-usage"}>
                                <React.Fragment>
                                    {billComparisonTotal.values[2]}
                                    <Tooltips position={this.tooltipPosition} tooltipText={this.getInfoDetails(billComparisonTotal.values[0], billComparisonTotal.values[1])}>
                                        <Info className={this.props.classes.infoIcon}></Info>
                                    </Tooltips>
                                </React.Fragment>
                            </TableCell>
                            {previousBillCycle && <TableCell colSpan={3} className={"bold fs-16 width-36 total-usage"}>
                                <React.Fragment>
                                    {billComparisonTotal.values[5]}
                                    <Tooltips position={this.tooltipPosition} tooltipText={this.getInfoDetails(billComparisonTotal.values[3], billComparisonTotal.values[4])}>
                                        <Info className={this.props.classes.infoIcon}></Info>
                                    </Tooltips>
                                </React.Fragment>
                            </TableCell>}
                            {/* {this.props.billComparisonTotal.values.map((item, index) => (
                        <TableCell className={"bold fs-16 total-usage" + index} key={index}>{item}</TableCell>
                    ))} */}
                            {previousBillCycle && <TableCell className={"bold fs-16 width-12 position-relative "}>
                                {billComparisonTotal.difference}
                                <ArrowDropUp className={classes.arrowIcon + (billComparisonTotal.difference > 0 ? ' color-red' : ' hidden')}></ArrowDropUp>
                                <ArrowDropDown className={classes.arrowIcon + (billComparisonTotal.difference < 0 ? ' color-green' : ' hidden')}></ArrowDropDown>
                                <span className={(billComparisonTotal.difference === 0 ? ' color-dark-blue' : ' hidden')}>
                                    <ArrowLeft className={classes.arrowIcon} style={{ right: 10 }}></ArrowLeft>
                                    <ArrowRight className={classes.arrowIcon} style={{ right: 2 }}></ArrowRight>
                                </span>
                            </TableCell>}
                        </TableRow>
                    </TableBody>
                </Table>
            )
        }
    }

    getInfoDetails(usage, discount) {
        return (
            <React.Fragment>
                <p><b>Usage/Rental : </b> {usage} </p>
                <p><b>Discount : </b> {discount} </p>
            </React.Fragment>
        )
    }

    removeNagetiveSign(val) {
        if (val !== "--") {
            return JSON.stringify(val).replace("-", "");
        }
        return val;
    }

    setPanelExapnsionState() {
        const obj = {};
        if (this.props.billComparisonData && this.props.billComparisonData.length) {
            for (let i = 0; i < this.props.billComparisonData.length; i++) {
                obj[this.props.billComparisonData[i]["name"]] = false;
            }
        }
        this.setState({ ...this.state, ...obj });
    }

    toggleButton(key) {
        this.setState({ [key]: !this.state[key] })
    }

    displayIcon(row) {
        if (row.items && row.items.length > 0) {
            return true;
        }
        return false;
    }
    getTableHeaders = () => {
        let { currentBillCycle, previousBillCycle } = this.props.data;
        let headers = this.headers
        if (previousBillCycle && currentBillCycle) {
            return [headers.OUTSTANDING_BREAKUP, headers.USAGE_RENTAL, headers.DISCOUNT, headers.NET_CHARGES, headers.USAGE_RENTAL, headers.DISCOUNT, headers.NET_CHARGES, headers.DIFFERENCES]
        }
        else if (!previousBillCycle) {
            return [headers.OUTSTANDING_BREAKUP, headers.USAGE_RENTAL, headers.DISCOUNT, headers.NET_CHARGES]
        }
    }


    getTableData = () => {
        let { currentBillCycle, billComparisonData, previousBillCycle } = this.props.data

        if (currentBillCycle && previousBillCycle) {
            return billComparisonData
        }
        else if (!previousBillCycle) {
            // remove values from table for previous bill cycle
            let newData = billComparisonData && JSON.parse(JSON.stringify(billComparisonData)).map(row => {
                row.values = row.values.filter((value, index) => index < 3)

                row.items = row.items.map(item => {
                    item.values = item.values.filter((value, index) => index < 3)
                    return item
                })

                return row
            })

            return newData
        }

    }

    render() {
        const { classes, handleCurrentBillCycle, handlePreviousBillCycle, tagBtnHandler, editBtnHandler } = this.props;
        const { openBillComparatorDialog, billComparisonData, closeBillComparatorRequest, currentBillCycle, previousBillCycle, billComparisonRecentDate, billComparisonPreviousDate, billCycle } = this.props.data;

        return (
            <React.Fragment>
                <Dialog
                    fullWidth={false}
                    maxWidth={previousBillCycle ? 'md' : 'sm'}
                    open={openBillComparatorDialog}
                    aria-labelledby="max-width-dialog-title"
                    className={classes.root}
                    id='bill-comparator-dialog'
                >
                    <DialogTitle id="max-width-dialog-title" className={classes.dialigTitle}>
                        <div className="position-relative pt-10 pl-20 pb-10">
                            <span onClick={editBtnHandler} className="cursor-pointer color-blue"> Bill Plan Comparison </span>
                            {/* <EditIcon className={"color-blue cursor-pointer " + classes.editIcon} onClick={editBtnHandler}></EditIcon> */}
                            <img src={ExternalIcon} className={classes.tagIcon} onClick={tagBtnHandler} alt="tagging" />
                            <div className={"close-dialog " + classes.closeDialog} onClick={closeBillComparatorRequest}><span>Close</span></div>
                        </div>
                        <div className={previousBillCycle ? classes.calendarContainer : classes.calendarContainerSingle} id="calenderRow">
                            <BillComparatorCalendar selectedMonth={currentBillCycle} billCycleHandler={handleCurrentBillCycle} name='currentBillCycle' billComparisonDate={billComparisonRecentDate} title="Current Bill Cycle" classProp={classes.currentBillCal} billCycle={billCycle}></BillComparatorCalendar>
                            {previousBillCycle && <BillComparatorCalendar selectedMonth={previousBillCycle} billCycleHandler={handlePreviousBillCycle} name='previousBillCycle' billComparisonDate={billComparisonPreviousDate} title="Previous Bill Cycle" classProp="" billCycle={billCycle}></BillComparatorCalendar>}
                        </div>
                    </DialogTitle>
                    <DialogContent className={classes.dialigContent}>
                        <Table className={classes.table} id="tableHeaderRow">
                            <TableHead>
                                <TableRow className="comparator-head">

                                    {this.getTableHeaders().map(header => (
                                        <TableCell className={header.class}>{header.label}</TableCell>
                                    ))}
                                </TableRow>
                            </TableHead>
                        </Table>
                        <div id="tableBodyScroll" className={classes.tableScroll}>
                            <Table className={classes.table}>
                                <TableBody>
                                    {this.getTableData().map(row => (
                                        <React.Fragment key={row.name}>
                                            <TableRow className="row-head">
                                                <TableCell className="name-head width-16" component="th" scope="row">
                                                    <div className="position-relative">
                                                        {row.name}
                                                        <AddCircle className={'color-blue ' + classes.expandIcon + (this.displayIcon(row) && !this.state[row.name] ? '' : ' hidden')} onClick={() => this.toggleButton(row.name)}>
                                                        </AddCircle>
                                                        <RemoveCircle className={'color-blue ' + classes.expandIcon + (this.displayIcon(row) && this.state[row.name] ? '' : ' hidden')} onClick={() => this.toggleButton(row.name)}>
                                                        </RemoveCircle>
                                                        <Tooltips position={this.tooltipPosition} tooltipText={AppConstant.TAX_CALCULATION_MESSAGE}>
                                                            <Info className={this.props.classes.expandIcon + (row.name === 'Taxes' ? '' : ' hidden ')}></Info>
                                                        </Tooltips>
                                                    </div>
                                                </TableCell>
                                                {row.values.map((item, index) => (
                                                    <TableCell key={index} className={'width-12 row-body-parent' + index}>{this.removeNagetiveSign(item)}</TableCell>
                                                ))}
                                                {previousBillCycle && <TableCell className={"body-parent-difference bold width-12 "}>
                                                    {row.difference}
                                                    <ArrowDropUp className={classes.arrowIcon + (row.difference > 0 ? ' color-red' : ' hidden')}></ArrowDropUp>
                                                    <ArrowDropDown className={classes.arrowIcon + (row.difference < 0 ? ' color-green' : ' hidden')}></ArrowDropDown>
                                                    <span className={(row.difference === 0 ? ' color-dark-blue' : ' hidden')}>
                                                        <ArrowLeft className={classes.arrowIcon} style={{ right: 10 }}></ArrowLeft>
                                                        <ArrowRight className={classes.arrowIcon} style={{ right: 2 }}></ArrowRight>
                                                    </span>
                                                </TableCell>}
                                            </TableRow>
                                            {row.items.map(items => (
                                                <TableRow key={items.name} className={(this.state[row.name] ? '' : 'hidden')}>
                                                    <TableCell className="name-child" component="th" scope="row">
                                                        <Tooltips position={this.tooltipPosition} tooltipText={items.name}>
                                                            <Typography variant="inherit" noWrap>
                                                                {items.name}
                                                            </Typography>
                                                        </Tooltips>
                                                    </TableCell>
                                                    {items.values.map((item, index) => (
                                                        <TableCell key={index} className={'row-body-child' + index}>{this.removeNagetiveSign(item)}</TableCell>
                                                    ))}
                                                    {previousBillCycle && <TableCell className={"body-child-difference bold "}>
                                                        {items.difference}
                                                        <ArrowDropUp className={classes.arrowIcon + (items.difference > 0 ? ' color-red' : ' hidden')}></ArrowDropUp>
                                                        <ArrowDropDown className={classes.arrowIcon + (items.difference < 0 ? ' color-green' : ' hidden')}></ArrowDropDown>
                                                        <span className={(items.difference === 0 ? ' color-dark-blue' : ' hidden')}>
                                                            <ArrowLeft className={classes.arrowIcon} style={{ right: 10 }}></ArrowLeft>
                                                            <ArrowRight className={classes.arrowIcon} style={{ right: 2 }}></ArrowRight>
                                                        </span>
                                                    </TableCell>}
                                                </TableRow>
                                            ))}
                                        </React.Fragment>
                                    ))}
                                </TableBody>
                            </Table>
                        </div>
                        {this.getTotal()}
                    </DialogContent>
                    {/* <DialogActions>
            <Button color="primary">
              Close
            </Button>
          </DialogActions> */}
                </Dialog>
            </React.Fragment>
        )
    }
}

export default withStyles(styles)(BillComparatorDialog);
