import React, { Component } from 'react';
import { connect } from 'react-redux';
import './universal-work-queue.style.css';
import TicketList from './ticket-list/ticket-list.smart';
import { toggleUWQWindowDisplay, fetchUWQRequest } from '../../actions/universal-work-queue.action.js';
import RefreshIcon from '@material-ui/icons/Refresh';
import RequestStatusConstant from '../../constants/request-status.constant';
import FilterListIcon from '@material-ui/icons/FilterList';
import Badge from '@material-ui/core/Badge';
import { openUWQFilterDialogRequest } from '../../actions/app-dialog.action';

class UniversalWorkQueue extends Component {

    constructor() {
        super();
        this.toggleDisplay = this.toggleDisplay.bind(this);
    }

    toggleDisplay() {
        this.props.toggleUWQWindowDisplay();
    }

    onClickRefresh = () => {
        this.props.fetchUWQRequest();
    }

    onClickFilter = () => {
        this.props.openUWQFilterDialogRequest();
    }

    render() {
        const { displayTickets, loadingStatus, filterCount } = this.props;
        return (
            <div className={displayTickets ? '' : 'hidden'}>
                <div className={"uwq-wrapper"}>
                    <div className="uwq-blocks pt-50 pr-40 pb-30 pl-30">
                        <div className="toolbar">
                            <Badge color="secondary" className="filter-badge" badgeContent={filterCount}>
                                <FilterListIcon className="filter-icon color-blue mr-20" onClick={this.onClickFilter} />
                            </Badge>
                            <RefreshIcon className={'refreshIconContainer color-blue mr-20 ' + (loadingStatus === RequestStatusConstant.LOADING ? 'refresh-rotate' : '')}
                                onClick={this.onClickRefresh} />
                            <div className={"close-dialog mr-20"}>
                                <span onClick={this.toggleDisplay}>Close</span>
                            </div>
                        </div>
                        <TicketList></TicketList>
                    </div>
                    <div className="clear"></div>
                </div>

                <div className="uwq-overlay-mask"></div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        displayTickets: state.uwq.displayTickets,
        loadingStatus: state.uwq.loadingStatus,
        filterCount: state.uwq.filterCount
    };
};

const mapDispatchToProps = {
    toggleUWQWindowDisplay,
    fetchUWQRequest,
    openUWQFilterDialogRequest
}


export default connect(mapStateToProps, mapDispatchToProps)(UniversalWorkQueue);
