import { FeedbackDialogActionType } from '../actiontype/feedback-dialog.actiontype';
import { DialogActionType } from '../actiontype/dialog.actiontype';
const initialState = {
  feedbackCallApiResponse: {},
  selectedTab: null,
  checkedTabs: 0,
  feedbackTypes: [
    {
        key: "System Status",
        problemAt: "System Status",
        checked: false,
        issueTypes: [
            "Incorrect Info", 
            "No Data Found"
        ],
        subProblems: [
            {
                key: "eCRM Status",
                name: "eCRM Status",
                status: null
            }, 
            {
                key: "Router Status",
                name: "Router Status",
                status: null
            },
            {
                key: "Link Status",
                name: "Link Status",
                status: null
            },
            {
                key: "Outage Status",
                name: "Outage Status",
                status: null
            },
            {
              key: "Lsi Ping Status",
              name: "Lsi Ping Status",
              status: null
            },
            {
              key: "Utilization Graph",
              name: "Utilization Graph",
              status: null
          }
        ],
        comments: ""
    },
    {
        key: "Product Details",
        problemAt: "Product Details",
        checked: false,
        issueTypes: [
            "Incorrect Info", 
            "No Data Found"
        ],
        subProblems: [
            {
                key: "Product",
                name: "Product",
                status: null
            }, 
            {
                key: "Platform",
                name: "Platform",
                status: null
            },
            {
                key: "Bandwidth",
                name: "Bandwidth",
                status: null
            },
            {
                key: "City Details",
                name: "City Details",
                status: null
            }
        ],
        comments: ""
    },
    {
        key: "Technical Details",
        problemAt: "Technical Details",
        checked: false,
        issueTypes: [
            "Incorrect Info", 
            "No Data Found"
        ],
        subProblems: [
            {
                key: "Router",
                name: "Router",
                status: null
            }, 
            {
                key: "WAN IP",
                name: "WAN IP",
                status: null
            },
            {
                key: "LAN IP",
                name: "LAN IP",
                status: null
            },
            {
                key: "Others",
                name: "Others",
                status: null
            }
        ],
        comments: ""
    },
    {
        key: "Others",
        problemAt: "Others",
        checked: false,
        issueTypes: [
            "Problem Exists",
        ],
        subProblems: [
            {
                key: "Landing Page Issues",
                name: "Landing Page Issues",
                status: null
            }, 
            {
                key: "Scenario Irregularities",
                name: "Scenario Irregularities",
                status: null
            },
            {
                key: "Prediction Not Working",
                name: "Prediction Not Working",
                status: null
            },
            {
                key: "Others",
                name: "Others",
                status: null
            }
        ],
        comments: ""
    }
]
};

function purgeFeedbackTypes(state) {
  let feedbackTypes = state.feedbackTypes;
  for(var i=0; i< feedbackTypes.length; i++) {
    feedbackTypes[i].checked = false;
    feedbackTypes[i].comments = "";
    for (var j=0; j<feedbackTypes[i].subProblems.length; j++) {
      feedbackTypes[i].subProblems[j] = {
          ...feedbackTypes[i].subProblems[j],
          status: null
      }
    }
  }
  state = {
        ...state,
        selectedTab: null,
        checkedTabs: 0,
        feedbackTypes: feedbackTypes
    }
  return state;
}
export default function feedbackDialogReducer(state = initialState, action) {
  switch (action.type) {
    case FeedbackDialogActionType.FEEDBACK_DIALOG_REQUEST:
      return {
        ...state, feedbackCallApiResponse: action.payload
      };
    case FeedbackDialogActionType.FEEDBACK_DIALOG_SUCCESS:
      return {
        ...state,
        feedbackCallApiResponse: action.payload
      };
    case FeedbackDialogActionType.FEEDBACK_DIALOG_FAILURE:
      return {
        ...state,
        feedbackCallApiResponse: action.payload
      };
    case FeedbackDialogActionType.SET_FEEDBACK_TYPES_DATA:
      return {
        ...state,
        feedbackTypes: [...action.payload]
      };
    case FeedbackDialogActionType.SET_CHECKED_VALUES_DATA:
      return {
        ...state,
        checkedTabs: action.payload
      };
    case FeedbackDialogActionType.SET_SELECTED_TAB_DATA:
      return {
        ...state,
        selectedTab: action.payload
      };
    case FeedbackDialogActionType.PURGE_FEEDBACK_DATA:
        return purgeFeedbackTypes(state);
    case DialogActionType.CLOSE_FEEDBACK_DIALOG:
    default:
      return state;
  }
}
