import { AppDialogActionType } from "../actiontype/app-dialog.actiontype";
import { AppDialogConstant } from '../constants/app-dialog.constant';
import CommonActionType from "../actiontype/common.actiontype";

const initialState = {
  openDialog: false,
  dialogTitle: '',
  maxWidth: 'sm'
};

export default (state = initialState, action) => {
  switch (action.type) {
    case AppDialogActionType.OPEN_DFF_REQUEST:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'md',
        dialogTitle: AppDialogConstant.DFF_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_CLAIM_DFF_REQUEST:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'md',
        dialogTitle: AppDialogConstant.CLAIM_DFF_DIALOG_TITLE
      };
    case AppDialogActionType.DISPLAY_FTR_MESSAGE:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.FTR_DIALOG_TITLE
      };
    case AppDialogActionType.DISPLAY_SR_MESSAGE:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.SR_DIALOG_TITLE
      };
    case AppDialogActionType.DISPLAY_DND_CATEGORIES:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.DND_DIALOG_TITLE
      };
    case AppDialogActionType.CREATE_FTR:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.CREATE_FTR_TITLE
      };
    case AppDialogActionType.CLOSE_APP_DIALOG:
      return {
        ...state,
        openDialog: initialState.openDialog
      };
    case AppDialogActionType.DISPLAY_ARC_SUCCESS_MESSAGE:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'xs',
        dialogTitle: AppDialogConstant.ARC_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_AUTH_MATRIX:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.AUTH_MATRIX_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_PAYMENT_HISTORY:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.PAYMENT_HISTORY_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_PROV_CONFIRM_DIALOG:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.PROV_CONFIRM_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_SR_NOTES_DETAILS_DIALOG:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'md',
        dialogTitle: AppDialogConstant.SR_NOTES_DETAILS_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_TASK_UPDATE_REQUEST:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'md',
        dialogTitle: AppDialogConstant.TASK_UPDATE_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_UWQ_FILTER_REQUEST:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'sm',
        dialogTitle: AppDialogConstant.UWQ_FILTER_DIALOG_TITLE
      };
    case AppDialogActionType.OPEN_TASK_UPDATE_CASE_SELECTOR_REQUEST:
      return {
        ...state,
        openDialog: true,
        maxWidth: 'md',
        dialogTitle: AppDialogConstant.TASK_UPDATE_CASE_SELECTOR_DIALOG_TITLE
      };
    case CommonActionType.RESET_STATE:
      return { ...state, ...initialState }
    default:
      return state;
  }
};
