import { DialogActionType } from "../actiontype/dialog.actiontype";

export const closeDialogRequest = data => ({
  type: DialogActionType.CLOSE_DIALOG,
  payload: { "open": false }
});

export const openCallDropDialog = data => ({
  type: DialogActionType.OPEN_CALLDROP_DIALOG,
  payload: { ...data, "open": true }
});

export const openFeedbackDialog = data => ({
  type: DialogActionType.OPEN_FEEDBACK_DIALOG,
  payload: { ...data, "open": true }
});
export const openSelfHelpDialog = data => ({
  type: DialogActionType.OPEN_SMS_DIALOG,
  payload: { ...data, "open": true }
});
export const openArcIssueDialog = data => ({
  type: DialogActionType.OPEN_ARC_ISSUE_DIALOG,
  payload: { ...data, "open": true }
});
export const closeArcIssueDialog = data => ({
  type: DialogActionType.CLOSE_ARC_ISSUE_DIALOG,
  payload: { ...data, "open": false }
});
export const openPreviousInteractionsDialog = data => ({
  type: DialogActionType.OPEN_PREVIOUS_INTERACTIONS_DIALOG,
  payload: { "open": true, title: "Previous Interactions", interactionsActiveTab: data }
});
export const closeFeedbackDialog = data => ({
  type: DialogActionType.CLOSE_FEEDBACK_DIALOG,
  payload: { ...data, "open": false }
});
export const closeCallDropDialog = data => ({
  type: DialogActionType.CLOSE_CALL_DROP_DIALOG,
  payload: { ...data, "open": false }
});
export const closeSRHistoryDialog = () => ({
  type: DialogActionType.CLOSE_SR_HISTORY_DIALOG,
  payload: { "open": false }
});
export const openSmsDialog = data => ({
  type: DialogActionType.OPEN_SMS_DIALOG,
  payload: { ...data, "open": true }
});
export const closeSmsDialog = data => ({
  type: DialogActionType.CLOSE_SMS_DIALOG,
  payload: { ...data, "open": false }
});
export const openCallTransferDialog = data => ({
  type: DialogActionType.OPEN_CALL_TRANSFER_DIALOG,
  payload: { ...data, "open": true }
});
export const closeCallTransferDialog = data => ({
  type: DialogActionType.CLOSE_CALL_TRANSFER_DIALOG,
  payload: { ...data, "open": false }
});
export const openMultipleTaggingDialog = data => ({
  type: DialogActionType.OPEN_MULTIPLE_TAGGING_DIALOG,
  payload: { ...data, "open": true }
});
export const closeMultipleTaggingDialog = data => ({
  type: DialogActionType.CLOSE_MULTIPLE_TAGGING_DIALOG,
  payload: { ...data, "open": false }
});
