import { SystemStatusActionType } from '../actiontype/b2b-system-status.actiontype';
import CommonActionType from '../actiontype/common.actiontype';

const initialState = {
    OUTAGE: "",
    LSI_STATUS: "",
    ROUTER: "",
    LINK_STATUS: "",
    refreshing: false,

}

export default function SystemStatusCardReducer(state = initialState, action) {
    switch (action.type) {
        case SystemStatusActionType.SYSTEM_STATUS_CARD_SUCCESS:
            let newState = action.payload;
            return {
                // ...state,
                ...newState
            }
        case SystemStatusActionType.REFRESH_STATE_LOADING:
            return {
                ...state,
                refreshing: true
            }
        case SystemStatusActionType.REFRESH_STATE_LOADING_COMPLETE:
            return {
                ...state,
                refreshing: false
            }
        case SystemStatusActionType.SHOW_LOADER:
            return {
                ...state,
                loader: true
            }
        case SystemStatusActionType.HIDE_LOADER:
            return {
                ...state,
                loader: false
            }
        case CommonActionType.RESET_STATE:
            return initialState;
        default:
            return state;
    }
}
