import React from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import styles from './customized-tabs.style';
import FiberManualRecord from '@material-ui/icons/FiberManualRecord';
import DisabledTaggingAction from '../../../actiontype/disabled-tagging.actiontype';

const CustomizedTabs = props => {
    let { classes, tabsRootCSS, markedChoiceColor, varianType, disabled } = props;
    tabsRootCSS = tabsRootCSS || "";

    return (
        <div className={classes.root}>
            <Tabs
                {...props.tabOptions}
                value={props.value}
                onChange={props.handleChange}
                variant={varianType}
                classes={{ root: classes.tabsRoot + " " + tabsRootCSS, indicator: classes.tabsIndicator }}
            >
                {Object.keys(props.tabList).map((item, index) => {
                    return (<Tab
                        disabled={disabled}
                        disableRipple
                        className={(props.tabList[item].display === 'false' ? ' hidden' : '')}
                        classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                        key={index}
                        id={item}
                        label={props.tabList[item].label}
                        icon={<FiberManualRecord className={classes.bulletCss + (markedChoiceColor && props.tabList[item].displayBullet === 'true' ? '' : ' hidden') + (markedChoiceColor === 'green' ? ' green' : ' red')} />}
                    />);
                })}
            </Tabs>
            <Typography component="div" style={{ height: props.tabContentHeight, overflowY: 'auto', position: 'relative' }} className={classes.typography}>{props.content}</Typography>
        </div>
    );
}

export default withStyles(styles)(CustomizedTabs);
