const styles = theme => ({
    root: {
        padding: '10px 25px'
    },
    formControl: {
        display: 'flex',
        alignItems: 'center',
        height: 60,
        '& p': {
            textAlign: 'right'
        }
    },
    refreshIconContainer: {
        margin: '1px 10px',
        height: '0.8em',
        width: '0.8em',
        '&:hover': {
            background: '#fff',
            cursor: 'pointer'
        }
    },
    copyIconStyle: {
        margin: '3px',
        '&:hover': {
            background: '#fff',
            cursor: 'pointer'
        }
    },
    icons: {
        position: 'absolute',
        top: 10,
        right: 0
    },
    head: {
        padding: 10
    }
});
export default styles;
