import React, { Component } from 'react';

import { fetchHandsetDetailsCardData } from '../../../../actions/handset-details-tab.action';
import PropTypes from 'prop-types';
import AppLoader from '../../AppLoader';
import { withStyles } from '@material-ui/core/styles';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { connect } from 'react-redux';
import { updateCurrentScenarioId } from '../../../../actions/scenario.action';
import { CardLinkConstant } from '../../../../constants/card-link.constant';
import { cardLinksHandler } from '../../../../actions/card-links.action';

class TechnicalDetails extends Component {

    constructor(props) {
        super(props);
        this.state = { cardLoader: true, expanded: true };
        this.populateHandsetDetails = this.populateHandsetDetails.bind(this);
        this.onExpansionChange = this.onExpansionChange.bind(this);
        this.onClickTagIcon = this.onClickTagIcon.bind(this);
    }

    onClickTagIcon(e, cardLinks) {
        e.stopPropagation();
        this.props.cardLinksHandler(cardLinks, CardLinkConstant.CRM_CODE_TYPE, CardLinkConstant.HANDSET_DETAILS_CARD);
    }
    handleHandsetDetailsScenario = (item) => {
        if (item && item.value) {
            this.props.updateCurrentScenarioId(item.value);
        }
    }
    populateHandsetDetails() {
        if (this.props && this.props.customerLSINumber) {
            this.showHandsetDetails = true;
            this.technicalDetails["Router"] = "HYD-HIT-MPL-PE-RTR-219";
            this.technicalDetails["Interface"] = "TenGigE0/1/1/3.8815";
            this.technicalDetails["WAN_IP"] = "172.41.2.70";
            this.technicalDetails["VRF_NAME"] = "MARUTI-S11";
            this.technicalDetails["ANG_CKT_ID"] = "BALW-T91874-78";
            this.technicalDetails["BTS_IP"] = "10.3.81.150 / 240";
            this.technicalDetails["CKT_ID"] = "MULX-ANGW-5405"

        } else {
            this.technicalDetails = {};
            this.technicalDetails["Router"] = "xxxx";
            this.technicalDetails["Interface"] = "xxxx";
            this.technicalDetails["WAN_IP"] = "xxxx";
            this.technicalDetails["VRF_NAME"] = "xxxx";
            this.technicalDetails["ANG_CKT_ID"] = "xxxx";
            this.technicalDetails["BTS_IP"] = "xxxx";
            this.technicalDetails["CKT_ID"] = "xxxx"
        }
    }


    onExpansionChange = () => (event, expanded) => {
        this.setState({ 'expanded': expanded });
    }

    componentDidUpdate(prevProps) {
        if (this.props.expandCard !== prevProps.expandCard) {
            this.setState({ 'expanded': this.props.expandCard });
        }
    }


    render() {
        const { classes } = this.props;
        const { HandsetDetailsCardDataResponse, loading, handsetScenarioCardLinksData } = this.props;
        const HandsetDetailsCardLinks = this.props.HandsetDetailsCardDataResponse && this.props.HandsetDetailsCardDataResponse.cardLinks;
        this.populateHandsetDetails();
        const showScenarioCardLinks = handsetScenarioCardLinksData && handsetScenarioCardLinksData && handsetScenarioCardLinksData.length ? true : false;
        return (
            <div className={"mt-10 " + (this.props.classes.card)}>
                <ExpansionPanel onChange={this.onExpansionChange()} expanded={this.state.expanded} >
                    <ExpansionPanelSummary className={this.props.classes.cardHeader + " card-header"} expandIcon={<ExpandMoreIcon />}>
                        <Typography className={classes.heading}>
                            TECHNICAL DETAILS
                        </Typography>
                    </ExpansionPanelSummary>
                    <ExpansionPanelDetails className={classes.panel + " card-data"}>
                        <div className="width-100">
                            <div className={classes.technicalDetails}>
                                <div className="width-100 inline-block text-left">
                                    <div className="fs-14 inline-block width-100 mb-20">
                                        <p className="color-grey fs-12 text-uppercase">Router</p>
                                        <p className="mt-5 fs-16 field-value">
                                            {this.technicalDetails['Router']}
                                        </p>
                                    </div>
                                    <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey fs-12 text-uppercase">Interface</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['Interface']}</p>
                                    </div>
                                    <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">Wan IP Address</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['WAN_IP']}</p>
                                    </div>
                                    <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">VRF NAME</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['VRF_NAME']}</p>
                                    </div>
                                    {/* <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">Lan IP Address</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['Volte']}</p>
                                    </div> */}
                                    {/* <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">IPS MPLS CKT ID</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['Volte']}</p>
                                    </div> */}
                                    {/* <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">FSP Name A Side</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['Volte']}</p>
                                    </div> */}
                                    <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">FSP CKT ID A Side</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['CKT_ID']}</p>
                                    </div>
                                    {/* <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">Enoc Hand Over</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['Volte']}</p>
                                    </div> */}
                                    <div className="fs-14 inline-block width-half mb-20">
                                        <p className="color-grey text-uppercase fs-12">Ang Circuit ID</p>
                                        <p className="mt-5 fs-16 field-value">{this.technicalDetails['ANG_CKT_ID']}</p>
                                    </div>
                                </div>
                            </div>
                            {loading ? <div><AppLoader cardLoader={this.state.cardLoader} /></div> : null}
                        </div>
                    </ExpansionPanelDetails>
                </ExpansionPanel>
            </div>
        )
    }
}

const styles = theme => ({
    root: {
        width: '100%',
    },
    heading: {
        fontSize: theme.typography.pxToRem(15),
        fontWeight: theme.typography.fontWeightRegular,
    },
    cardHeader: {
        'border-bottom': 'solid #ccc 0.5px',
    },
    tagIcon: {
        top: '5px',
        position: 'relative',
        margin: '0px 8px',
        textAlign: 'center',
        cursor: 'pointer',
        width: 15,
    },
    benefitsIcon: {
        maxHeight: '35px',
        verticalAlign: 'middle',
    },
    cardContainer: {
        border: 'solid #ccc 0.5px',
    }
});

const mapStateToProps = state => {
    return {
        HandsetDetailsCardDataResponse: state.HandsetDetailsCardData.HandsetDetailsCardDataResponse,
        isCustomerFound: state.customerInfo.isCustomerFound,
        loading: state.HandsetDetailsCardData.HandsetDetailsCardStatus === 'LOADING',
        customerInfo: state.customerInfo.customerInfo,
        handsetScenarioCardLinksData: state.HandsetDetailsCardData.handsetScenarioCardLinksData,
        customerLSINumber: state.customerInfo.customerLSINumber,

    }
}
const mapDispatchToProps = dispatch => ({
    fetchHandsetDetailsCardData: data => dispatch(fetchHandsetDetailsCardData(data)),
    updateCurrentScenarioId: id => dispatch(updateCurrentScenarioId(id)),
    cardLinksHandler: (data, type, key) => dispatch(cardLinksHandler(data, type, key)),
});

TechnicalDetails.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(TechnicalDetails));
