import React from 'react';
import CustomizedTabs from '../../common/customized-tabs/customized-tabs.dumb';
import { connect } from 'react-redux';
import { switchHomeTab, setHomeTab } from '../../../actions/tabs.action';
import { withStyles } from '@material-ui/core/styles';
import styles from './home-tabs.style';
import AnalyticsService from '../../../services/analytics-service';

const tabOptions = {
  scrollButtons: "auto",
  // scrollable: true,
  variant: 'scrollable'
}

class HomeTabs extends React.Component {

  constructor(props) {
    super(props)
    const homepageTabToAnalyticsKeyMapping = {
      HOME_B2B_UTILIZATION_TAB: 'utilization',
      HOME_SR_DETAILS_TAB: 'sr_details',
      LSI_PING_STATUS: 'lsi_ping_status',
      HOME_SR_SUMMARY_TAB: 'sr_summary',
      HOME_SR_HISTORY_TAB: 'sr_history',
    }
    this.state = {
      homepageTabToAnalyticsKeyMapping
    }
  }
  handleChange = (event, value) => {
    const id = event.currentTarget.id;
    AnalyticsService.trackEvent({
      eVar9: `b2b|dt|homepage|${this.state.homepageTabToAnalyticsKeyMapping[id]}|click`,
      eVar8: 'b2b|dt|homepage|updated'
    })
    this.props.switchHomeTab(id);
  };

  getTabContent(key) {
    const { homeTabList } = this.props;
    if (key) {
      const tabObj = homeTabList[key];
      const obj = {
        index: Object.keys(homeTabList).indexOf(key),
        content: tabObj.component
      }
      return obj;
    }
    return {};
  }

  render() {
    const { classes, homeActiveTab, homeTabList, disableTabs } = this.props;
    const { index, content } = this.getTabContent(homeActiveTab);
    const tabContentHeight = `calc(100vh - 321px)`;
    const varianType = 'scrollable';
    return (
      <div className="tabs-cont">
        <CustomizedTabs disabled={disableTabs} value={index} tabsRootCSS={classes.tabsRoot} tabOptions={tabOptions} handleChange={this.handleChange.bind(this)} tabList={homeTabList} content={content} tabContentHeight={tabContentHeight} varianType={varianType}></CustomizedTabs>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    homeActiveTab: state.tab.homeActiveTab,
    homeTabList: state.tab.homeTabList,
    disableTabs: state.scenario.searchDisabled
  };
};

const mapDispatchToProps = dispatch => ({
  switchHomeTab: index => dispatch(switchHomeTab(index)),
  setHomeTab: data => dispatch(setHomeTab(data))
});

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(HomeTabs));
