import { UsageDetailActionType } from '../actiontype/usage-details-tab.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';
const initialState = {
    UsageDetailsTabStatus: '',
    usageDetailsTabApiResponse: {},
    usageAddOnDetailsResponse: {},
    UsageAddOnDetailsStatus: {},
    usageScenarioCardLinksData: {},

};

export default function UsageDetailsTabReducer(state = initialState, action) {
    switch (action.type) {
        case UsageDetailActionType.USAGE_DETAILS_TAB_REQUEST:
            return {
                ...state,
                UsageDetailsTabStatus: "LOADING",
                usageDetailsTabApiResponse: action.payload
            };
        case UsageDetailActionType.USAGE_DETAILS_TAB_SUCCESS:
            return {
                ...state,
                UsageDetailsTabStatus: true,
                usageDetailsTabApiResponse: action.payload
            };
        case UsageDetailActionType.USAGE_DETAILS_TAB_FAILURE:
            return {
                ...state,
                UsageDetailsTabStatus: false,
            };


        case UsageDetailActionType.USAGE_ADD_ON_DETAILS_REQUEST:
            return {
                ...state,
                UsageAddOnDetailsStatus: "loading",
                usageAddOnDetailsResponse: action.payload
            };
        case UsageDetailActionType.USAGE_ADD_ON_DETAILS_SUCCESS:
            return {
                ...state,
                UsageAddOnDetailsStatus: true,
                usageAddOnDetailsResponse: action.payload
            };
        case UsageDetailActionType.USAGE_ADD_ON_DETAILS_FAILURE:
            return {
                ...state,
                UsageAddOnDetailsStatus: false,
            };
        case UsageDetailActionType.USAGE_DETAILS_SCENARIO_CARD_LINKS:
            return {
                ...state,
                usageScenarioCardLinksData: action.payload
            };


        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }
        default:
            return state
    }

}
