import { TabsActionType } from "../actiontype/tabs.actiontype";
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";
import { TabConstant } from '../constants/tabs.constant';
import React from 'react';
import BillCompare from '../components/home/bill-compare/bill-compare.smart';
import UsageDetails from '../components/home/usage-details/usage-details.smart';
import Recommendations from '../components/home/smart/Recommendations';
import Scenario from '../components/scenario/scenario.smart';
import TopScenario from '../components/home/top-scenario/top-scenario.smart';
import SystemChecks from '../components/scenario/system-checks/system-checks.smart';
import SystemInfo from '../components/scenario/system-info/system-info.smart';
import CommonAction from "../actiontype/common.actiontype";
import LegacyAlert from '../components/home/legacy-alert/legacy-alert.dumb';
import SRDetails from '../components/home/sr-details/sr-details.smart';
import SRSummaryTab from '../components/home/sr-summary-tab/sr-summary-tab.smart';
import UtilizationSummaryTab from '../components/home/b2b-utilization-tab/utilization-summary-tab.smart';
import SRNotesDetails from '../components/home/sr-details/sr-notes-details/sr-notes-details.smart';
import CreateSRNotes from '../components/home/sr-details/create-sr-notes/create-sr-notes.smart';
import LsiPingStatus from '../components/home/sr-details/lsi-ping-status/lsi-ping-status.smart';
import SRHistoryTab from '../components/home/sr-history-tab/sr-history-tab.smart';

const homeTabList = {
  [TabConstant.HOME_USAGE_DETAILS_TAB]: {
    label: "PLAN DETAILS",
    component: <UsageDetails />,
    display: 'false'
  },
  [TabConstant.HOME_TOP_SCENARIO_TAB]: {
    label: "TOP SCENARIOS",
    component: <TopScenario />,
    display: 'false'
  },
  [TabConstant.HOME_BILL_COMPARE_TAB]: {
    label: "BILL COMPARE",
    component: <BillCompare />,
    display: 'false'
  },
  [TabConstant.HOME_RECOMMENDATION_TAB]: {
    label: "RECOMMENDATIONS",
    component: <Recommendations />,
    display: 'false'
  },
  [TabConstant.HOME_SR_DETAILS_TAB]: {
    label: "SR DETAILS",
    component: <SRDetails />,
    display: 'true'
  },
  [TabConstant.LSI_PING_STATUS]: {
    label: "LSI Ping Status",
    component: <LsiPingStatus />,
    display: 'true'
  },
  [TabConstant.HOME_ALERT_TAB]: {
    label: "ALERT",
    component: <LegacyAlert />,
    display: 'false'
  },
  [TabConstant.HOME_SCENARIO_TAB]: {
    label: "SCENARIO",
    component: <Scenario />,
    display: 'false'
  },
  [TabConstant.HOME_B2B_UTILIZATION_TAB]: {
    label: "UTILIZATION",
    component: <UtilizationSummaryTab />,
    display: 'true'
  },
  [TabConstant.HOME_SR_SUMMARY_TAB]: {
    label: "SR SUMMARY",
    component: <SRSummaryTab />,
    display: 'true'
  },
  [TabConstant.HOME_SR_HISTORY_TAB]: {
    label: "SR HISTORY",
    component: <SRHistoryTab />,
    display: 'true'
  }

}

const b2bSystemTabs = [TabsActionType.DISPALY_SR_DETAILS_TAB, TabsActionType.DISPLAY_SR_SUMMARY_TAB, TabsActionType.DISPLAY_UTILIZATION_SUMMARY_TAB, TabsActionType.DISPLAY_SR_HISTORY_TAB];
const siebelSystemTabs = [TabsActionType.DISPALY_USAGE_TAB, TabsActionType.HIDE_TOP_SCENARIO_TAB, TabsActionType.DISPALY_BILL_COMPARE_TAB, TabsActionType.DISPALY_SR_DETAILS_TAB, TabsActionType.HIDE_RECOMMENDATION_TAB, TabsActionType.HIDE_SCENARIO_TAB, TabsActionType.HIDE_ALERT_TAB];
const otherSystemTabs = [TabsActionType.HIDE_USAGE_TAB, TabsActionType.DISPALY_TOP_SCENARIO_TAB, TabsActionType.HIDE_BILL_COMPARE_TAB, TabsActionType.HIDE_SR_DETAILS_TAB, TabsActionType.HIDE_RECOMMENDATION_TAB, TabsActionType.HIDE_SCENARIO_TAB, TabsActionType.HIDE_ALERT_TAB];
const prospectSystemTabs = [TabsActionType.HIDE_USAGE_TAB, TabsActionType.HIDE_TOP_SCENARIO_TAB, TabsActionType.HIDE_BILL_COMPARE_TAB, TabsActionType.HIDE_SR_DETAILS_TAB, TabsActionType.HIDE_RECOMMENDATION_TAB, TabsActionType.HIDE_SCENARIO_TAB, TabsActionType.HIDE_ALERT_TAB];
const legacySystemTabs = [TabsActionType.HIDE_USAGE_TAB, TabsActionType.HIDE_TOP_SCENARIO_TAB, TabsActionType.HIDE_BILL_COMPARE_TAB, TabsActionType.HIDE_SR_DETAILS_TAB, TabsActionType.HIDE_RECOMMENDATION_TAB, TabsActionType.HIDE_SCENARIO_TAB, TabsActionType.DISPALY_ALERT_TAB];

const scenarioTabList = {
  [TabConstant.SCENARIO_SYSTEM_INFO_TAB]: {
    label: "SYSTEM INFO",
    component: <SystemInfo />
  },
  [TabConstant.SCENARIO_SYSTEM_CHECK_TAB]: {
    label: "SYSTEM CHECKS",
    component: <SystemChecks />,
    displayBullet: 'true'
  }
}

const srNotesTabList = {
  [TabConstant.SR_NOTES_DETAILS]: {
    label: "Notes",
    component: <SRNotesDetails />,
    display: 'true'
  },
  [TabConstant.SR_CREATE_NOTES]: {
    label: "Create Note",
    component: <CreateSRNotes />,
    display: 'true'
  }
}

const initialState = {
  homeActiveTab: TabConstant.HOME_USAGE_DETAILS_TAB,
  homeTabList: homeTabList,
  scenarioActiveTab: TabConstant.SCENARIO_SYSTEM_INFO_TAB,
  scenarioTabList: scenarioTabList,
  siebelSystemTabs,
  otherSystemTabs,
  prospectSystemTabs,
  legacySystemTabs,
  b2bSystemTabs,
  srNotesActiveTab: TabConstant.SR_NOTES_DETAILS,
  srNotesTabList
};

export default (state = initialState, action) => {
  switch (action.type) {
    case TabsActionType.SWITCH_HOME_TAB:
      return {
        ...state,
        homeActiveTab: action.payload
      };
    case TabsActionType.SET_HOME_TAB:
      return {
        ...state,
        homeTabList: action.payload
      };
    case TabsActionType.SWITCH_SCENARIO_TAB:
      return {
        ...state,
        scenarioActiveTab: action.payload
      };
    case TabsActionType.SET_SCENARIO_TAB:
      return {
        ...state,
        scenarioTabList: action.payload
      };
    case TabsActionType.DISPALY_TOP_SCENARIO_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_TOP_SCENARIO_TAB]: { ...state.homeTabList[TabConstant.HOME_TOP_SCENARIO_TAB], display: 'true' } }
      };
    case TabsActionType.HIDE_TOP_SCENARIO_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_TOP_SCENARIO_TAB]: { ...state.homeTabList[TabConstant.HOME_TOP_SCENARIO_TAB], display: 'false' } }
      };
    case TabsActionType.DISPALY_BILL_COMPARE_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_BILL_COMPARE_TAB]: { ...state.homeTabList[TabConstant.HOME_BILL_COMPARE_TAB], display: 'true' } }
      };
    case TabsActionType.HIDE_BILL_COMPARE_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_BILL_COMPARE_TAB]: { ...state.homeTabList[TabConstant.HOME_BILL_COMPARE_TAB], display: 'false' } }
      };
    case TabsActionType.DISPALY_USAGE_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_USAGE_DETAILS_TAB]: { ...state.homeTabList[TabConstant.HOME_USAGE_DETAILS_TAB], display: 'true' } }
      };
    case TabsActionType.HIDE_USAGE_TAB:
      return {
        ...state,
        homeTabList: { ...state.homeTabList, [TabConstant.HOME_USAGE_DETAILS_TAB]: { ...state.homeTabList[TabConstant.HOME_USAGE_DETAILS_TAB], display: 'false' } }
      };
    case TabsActionType.DISPALY_SR_DETAILS_TAB:
      return {
        ...state,
