import { LoginActionType } from "../actiontype/login.actiontype";
import LoginService from '../services/login.service';
import { openSnackbarRequest } from './snackbar.action';
import { SnackbarMessages } from '../constants/snackbar-messages';
import CookieManagerService from "../services/cookie.service"
import { authorizeUser, unauthorizeUser } from '../actions/authorization.actions'
import { resetState } from "../actions/common.action";
import ApiService from '../services/api.service';
import { enableAllTaggings } from './disabled-tagging.action';
import  AnalyticsService from '../services/analytics-service';

let snackbarMessage = {}

export const fetchLoginData = loginObj => dispatch => {
    dispatch(resetState());
    dispatch(loginApiLoading());
    LoginService.fetchLoginApi(loginObj).then(
        response => {
            if (response && response.data) {
                LoginService.loginDataHandler(response.data);
                dispatch(authorizeUser())
                dispatch(loginApiSuccess(response));
                dispatch(openSnackbarRequest(null, SnackbarMessages.LOGIN_SUCCESS_MSG));
            }
        },
        error => {
            dispatch(unauthorizeUser());
            if (error && error['response'] && error['response']['data'] && error['response']['data']['error_description']) {
                if(error['response']['data']["error"] === "invalid_token"){
                    snackbarMessage = "Session Timed Out";
                } else {
                    snackbarMessage = error['response']['data']['error_description'];
                }
            }
            else {
                snackbarMessage = SnackbarMessages.LOGIN_FAILURE;
            }
            dispatch(loginApiFailure(error));
            dispatch(openSnackbarRequest(null, snackbarMessage));
        }
    );
}
export const handleLogout = () => dispatch => {
    dispatch(unauthorizeUser());
    dispatch(resetState());
    const token = CookieManagerService.getItem('AUTH_DETAILS');
    if (!token) {
        return;
    }
    //enable all taggings from sidebar and FTr and SR
    dispatch(enableAllTaggings());

    AnalyticsService.trackEvent({
        eVar9: "b2b|dt|homepage|logout|click",
        eVar8: "b2b|dt|homepage",
        prop21: null,
        prop22: null,
        prop23: null,
        prop24: null,
        prop25: null,
        prop26: null,
        prop27: null,
        prop28: null,
        prop29: null,
        prop30: null,
        prop31: null,
        prop32: null,
        prop33: null,
    })

    ApiService.userLogout().then(
        response => {
            snackbarMessage = SnackbarMessages.LOGOUT_SUCCESS;
            dispatch(openSnackbarRequest(null, snackbarMessage));
        },
        error => {
            if (error && error['response'] && error['response']['data'] && error['response']['data']['error_description']) {
                if(error['response']['data']["error"] === "invalid_token"){
                    snackbarMessage = "Session Timed Out";
                } else {
                    snackbarMessage = error['response']['data']['error_description'];
                }
            }
            else {
                snackbarMessage = SnackbarMessages.LOGIN_FAILURE;
            }
            dispatch(openSnackbarRequest(null, snackbarMessage));
        }
    );
    CookieManagerService.clearCookie('AUTH_DETAILS')
}

function loginApiLoading() {
    return {
        type: LoginActionType.LOGIN_REQUEST
    }
}
export function loginApiSuccess(response) {
    return {
        type: LoginActionType.LOGIN_SUCCESS,
        payload: response.data
    }
}
export function loginApiFailure(error) {
    return {
        type: LoginActionType.LOGIN_FAILURE,
        payload: error
    }
}


export const logoutUser = () => dispatch => {
    dispatch({
        type: LoginActionType.LOGOUT
    })
}
