import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import AutoComplete from '../common/AutoComplete';
import HomeTabs from '../home/home-tabs/home-tabs.smart';
import ScenarioTabs from '../scenario/scenario-tabs/scenario-tabs.smart';
import { connect } from 'react-redux';
import { TabConstant } from '../../constants/tabs.constant';
// import ScenarioBreadcrumb from '../scenario/ScenarioBreadcrumb';
import MainContentRight from './main-content-right/main-content-right.dumb'

const styles = theme => ({
    mainContent: {
        top: 160,
        textAlign: 'center',
        display: 'flex'
    },
    flexContainer: {
        width: '100%',
        display: 'flex'
    },
    leftPanel: {
        width: '70%'
    },
    rightPanel: {
        width: '30%',
        // minHeight: 'calc(100vh - 160px)',
        background: '#eaeaea',
        padding: 10
    },
    stickySearchBar: {
        backgroundColor: '#fafafa',
        position: 'sticky',
        padding: '10px',
        top: '0',
        zIndex: 3
    }
});

class MainContent extends Component {

    isScenarioTabActive() {
        if (this.props.homeActiveTab === TabConstant.HOME_SCENARIO_TAB) {
            return true;
        }
        return false;
    }

    // displayScenarioTabs() {
    //     const { scenarioSystemCheckList, scenarioSystemInfoList } = this.props;
    //     const flag = (scenarioSystemCheckList.length || scenarioSystemInfoList.length || this.isScenarioTabActive()) ? true : false;
    //     return flag;
    // }

    displayScenarioTabs() {
        const flag = this.isScenarioTabActive() ? true : false;
        return flag;
    }

    render() {
        const { classes } = this.props;
        return (
            <div className={classes.mainContent + ' overflow-y-auto'}>
                <div className={classes.flexContainer}>
                    <div className={classes.leftPanel + ' overflow-y-auto'}>
                        {/* <ScenarioBreadcrumb /> */}
                        <div className={classes.stickySearchBar}><AutoComplete></AutoComplete></div>
                        <HomeTabs></HomeTabs>
                    </div>
                    <div className={classes.rightPanel + ' overflow-y-auto'}>
                        <div className={this.displayScenarioTabs() ? '' : ' hidden '}>
                            <ScenarioTabs />
                        </div>
                        <MainContentRight expandCard={!this.displayScenarioTabs()} />

                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        homeActiveTab: state.tab.homeActiveTab,
        homeTabList: state.tab.homeTabList,
        customerInfo: state.customerInfo.customerInfo,
        scenarioSystemCheckList: state.scenario.scenarioSystemCheckList,
        scenarioSystemInfoList: state.scenario.scenarioSystemInfoList
    };
};


export default connect(mapStateToProps, null)(withStyles(styles, { withTheme: true })(MainContent));
