import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import EncodeDecodeType from '../actiontype/encode-decode-type';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';

class SRService {

    static scenarioSRRequest(reqData) {
        let authDetails = CookieManagerService.getAuthDetails();
        const data = {
            channel: authDetails.channel,
            lob: authDetails.lob,
            source: authDetails.source
        };
        const reqDataObj = Object.assign({}, data, reqData);
        var url = Config.contextPath + "user/rest/v1.0/tag/b2b/sr";
        return CustomHttpService.post(url, reqDataObj, {}, EncodeDecodeType.DEFAULT);
    }

    static insuranceClaimRequest(reqData) {
        let authDetails = CookieManagerService.getAuthDetails();
        const data = {
            channel: authDetails.channel,
            lob: authDetails.lob,
            source: authDetails.source,
            agentId: authDetails.agentId,
            customerMsisdn: CookieManagerService.getItem(AppConstant.MSISDN)
        };
        const reqDataObj = Object.assign({}, data, reqData);
        var url = Config.contextPath + "user/rest/v1.0/tag/postpaid/siebel/insuranceClaim";
        return CustomHttpService.post(url, reqDataObj, {}, EncodeDecodeType.DEFAULT);
    }
}

export default SRService;
