const styles = theme => ({
    textField: {
        width: '77%',
        display: 'inline-block',
        top: '-15px',
        position: 'relative',
        '& input': {
            paddingRight: 30
        }
    },
    list: {
        width: '77%',
        position: 'absolute',
        zIndex: '1',
        top: 50,
        backgroundColor: theme.palette.background.paper,
        padding: 0,
        '& > div:hover': {
            backgroundColor: '#fff'
        }
    },
    arrow: {
        fontSize: 30,
        top: 15,
        position: 'relative',
        cursor: 'pointer',
        left: '-25px'
    },
    btnContainer: {
        margin: 20,
        textAlign: 'right'
    },
    table: {
        border: 'none !important',
        '& thead th': {
            fontSize: 16
        },
        '& th, td': {
            borderBottom: '1px solid #ccc'
        },
        '& tr': {
            height: 40
        }
    },
    choiceBtn: {
        margin: 4,
        background: '#fff',
        color: '#03A9F4',
        width: 130,
        fontSize: 12,
        border: '1px solid #03A9F4',
        '&:hover': {
            background: '#fff',
            border: '1px solid #03A9F4'
        }
    },
    panelDetails: {
        padding: 0
    }
})

export default styles;
