const styles = theme => ({
    paper: {
        padding: theme.spacing.unit * 2,
        color: theme.palette.text.secondary,
        boxShadow: 'none'
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing.unit,
        top: theme.spacing.unit,
        color: theme.palette.grey[500],
    },
    buttonHeading: {
        ...theme.typography.button,
        backgroundColor: theme.palette.common.white,
        padding: theme.spacing.unit,
        color: '#03a9f4',
        paddingLeft: '0px;',
        textTransform: 'capitalize',
    },
    radio: {
        color: '#4BA6EE',
        '&$checked': {
            color: '#4BA6EE',
        }
    },
    check: {
        color: '#4BA6EE',
        '&$checked': {
            color: '#4BA6EE',
        }
    },
    customScrollableBox: {
        overflow: 'scroll',
        maxHeight: '200px',
        padding: '10px',
        marginTop: '10px',
        boxShadow: 'none',
    },
    mt40: {
        marginTop: '28px !important'
    },
    group: {
        display: 'block'
    },
    titlebarSms: {
        backgroundColor: '#ccc',
        color: '#000',
        padding: '5px',
        textAlign: 'center',
        marginTop: '20px'
    },
    taggingblock: {
        width: '46%',
        padding: '10px',
        border: '1px solid #03a9f4',
        display: 'inline-block',
        margin: '5px 10px 5px 0',
        height: '50px',
        position: 'relative'
    },
    tagIcon: {
        top: '2px',
        position: 'relative',
        margin: '0px 5px',
        textAlign: 'center',
        width: 15,
        cursor: 'pointer'
    },
    breakWord: {
        wordBreak: 'break-word'
    },
    checked: {},
    heading: {
        fontSize: theme.typography.pxToRem(15),
        fontWeight: theme.typography.fontWeightRegular,
    }


});
export default styles;
