import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';

import EncodeDecodeType from '../actiontype/encode-decode-type';
class PaymentDetailsCardService {
    static getPaymentDetails() {
        const config = {
            headers: {
                'Content-Type': "application/x-www-form-urlencoded",
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        const channel = authDetails.channel || "";
        // var url = "https://demo8694544.mockable.io/paymentDetails";
        var url = Config.contextPath + "postpaid/siebel/card/paymentDetails?channel=" + channel + "&transactionId=" + transactionId;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }
    static submitPaymentHistorySmsApi(selectedPaymentHistoryDetailArr) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const channel = authDetails.channel || "";
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        // var url = "http://demo8694544.mockable.io/paymentDetails2";
        var url = Config.contextPath + "postpaid/siebel/send-payment-receipts?channel=" + channel + "&transactionId=" + transactionId;
        return CustomHttpService.post(url, selectedPaymentHistoryDetailArr, config, EncodeDecodeType.DEFAULT);
    }
}

export default PaymentDetailsCardService;
