import { CallTransferDialogActionType } from '../actiontype/call-transfer-dialog.actiontype';
const initialState = {
    //fetching call transfer details
    fetchCallTransferApiResponse: {},
    fetchCallTransferCategories: {},
    fetchCallTransferSubCatgoryApiResponse: {},
};

export default function callTransferDialogReducer(state = initialState, action) {
    switch (action.type) {

        case CallTransferDialogActionType.FETCH_CALL_TRANSFER_DIALOG_REQUEST:
            return {
                ...state, fetchCallTransferApiResponse: action.payload
            };
        case CallTransferDialogActionType.FETCH_CALL_TRANSFER_DIALOG_SUCCESS:
            return {
                ...state,
                fetchCallTransferCategories: action.payload
            };
        case CallTransferDialogActionType.FETCH_CALL_TRANSFER_DIALOG_FAILURE:
            return {
                ...state,
                fetchCallTransferApiResponse: action.payload
            };
        case CallTransferDialogActionType.FETCH_CALL_TRANSFER_SUBCATEGORY:
            return {
                ...state,
                fetchCallTransferSubCatgoryApiResponse: action.payload
            };
        default:
            return state
    }
}
