import React, { Component } from 'react'
import { withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import { connect } from 'react-redux'
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import './alert-message-dialog.style.css';
import { openAlertMessageDialog, closeAlertMessageDialog } from '../../../actions/alert-message-dialog.action';

class AlertMessageDialog extends Component {

    handleClose = () => {
        this.props.closeAlertMessageDialog()
    };

    render() {
        let { open, classes, message } = this.props;

        return (
            <Dialog open={open} fullWidth={true} maxWidth='sm' onClose={this.handleClose} aria-labelledby="simple-dialog-title" >
                <IconButton className="closeButton" >
                    <CloseIcon onClick={this.handleClose} />
                </IconButton>
                <div className="alert-dialog">
                    <DialogContent>
                        <DialogContentText className={classes.content}>{message}</DialogContentText>
                    </DialogContent>
                </div>
            </Dialog>
        )
    }
}

const mapStateToProps = state => {
    return {
        message: state.alertMessageDialog.message,
        open: state.alertMessageDialog.open
    }
}
const mapDispatchToProps = {
    openAlertMessageDialog, closeAlertMessageDialog
}


export default connect(mapStateToProps, mapDispatchToProps)(withStyles()(AlertMessageDialog))
