import { SRDetailActionType } from '../actiontype/sr-details-tab.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';
const initialState = {
  SRDetailsTabStatus: '',
  srDetailsTabApiResponse: {},
  expanded: 'panel1',
  expandedRowIndex: -1,
  srDetailsCategorySpecificData: null,
  srNotesApiResponse: null,
  srNotesDetails: [],
  notesTypeList: [
    "Update To Partner",
    "L1 Update",
    "Follow up update",
    "Channel Partner Update",
    "Channel Partner Note",
    "Closure update",
    "Escalation update",
    "Partner Update",
    "Resolution update",
    "Customer call update",
    "General Note"],
  currentViewSrNoteId: '',
  createNoteForm: {
    noteType: '',
    visibility: '',
    noteDetails: '',
    description: ''
  },
  noteDetailsCharacterLimit: 3990,
  noteDescriptionCharacterLimit: 1900,
  srNotesContents: {},
  srDetailsProcessedData: {}
};

export default function SRDetailsTabReducer(state = initialState, action) {
  switch (action.type) {
    case SRDetailActionType.SR_DETAILS_TAB_REQUEST:
      return {
        ...state,
        SRDetailsTabStatus: "loading",
        showSRHistory: true,
        srDetailsTabApiResponse: action.payload
      };
    case SRDetailActionType.SR_DETAILS_TAB_SUCCESS:
      return {
        ...state,
        SRDetailsTabStatus: true,
        srDetailsTabApiResponse: action.payload
      };
    case SRDetailActionType.UPDATE_SR_DETAILS_PROCESSED_DATA:
      return {
        ...state,
        srDetailsProcessedData: action.payload
      }
    case SRDetailActionType.SR_DETAILS_TAB_FAILURE:
      return {
        ...state,
        SRDetailsTabStatus: false,
      };
    case SRDetailActionType.SET_EXPNSION_PANEL_ROW_INDEX:
      return {
        ...state,
        expandedRowIndex: action.payload,
      };
    case SRDetailActionType.SET_DEFAULT_EXPNSION_ROW_INDEX:
      return {
        ...state,
        expandedRowIndex: action.payload,
      };
    case SRDetailActionType.SET_CATEGORY_SPECIFIC_SR_FTR_DATA:
      return {
        ...state,
        srDetailsCategorySpecificData: action.payload
      }
    case SRDetailActionType.SHOW_SR_ADDITIONAL_DETAILS:
      return {
        ...state,
        showSRHistory: false,
        srAdditionalDetails: action.payload
      }
    case SRDetailActionType.SHOW_SR_HISTORY_TAB:
      return {
        ...state,
        showSRHistory: true
      }
    case SRDetailActionType.UPDATE_SR_NOTES_API_RESPONSE:
      return {
        ...state,
        srNotesApiResponse: action.payload
      }
    case SRDetailActionType.UPDATE_CREATE_NOTE_FORM_VALUES:
      return {
        ...state,
        createNoteForm: { ...state.createNoteForm, ...action.payload }
      }
    case SRDetailActionType.UPDATE_CURRENT_VIEW_SR_NOTES_ID:
      return {
        ...state,
        currentViewSrNoteId: action.payload
      }
    case SRDetailActionType.RESET_CREATE_NOTE_FORM_VALUES:
      return {
        ...state,
        createNoteForm: { ...initialState.createNoteForm }
      }
    case CustomerInfoActionType.RESET_CUSTOMER:
    case CommonActionType.RESET_STATE:
      return {
        ...state,
        ...initialState
      }
    case SRDetailActionType.SR_DETAILS_RESET_REQUEST:
      return {
        ...state,
        srDetailsTabApiResponse: {},
        srDetailsCategorySpecificData: {}
      }
    case SRDetailActionType.UPDATE_SR_NOTES_DETAILS:
      return {
        ...state,
        srNotesDetails: action.payload
      }
    case SRDetailActionType.UPDATE_SR_NOTES_CONTENTS:
      const id = action.payload.noteId;
      const value = action.payload.noteDetails;
      return {
        ...state,
        srNotesContents: {
          ...state.srNotesContents,
          [id]: value
        }
      }
    default:
      return state
  }
}
