import { MultipleTaggingActionType } from "../actiontype/multiple-tagging.actiontype";

const initialState = {
    multipleTaggingObject: {},
    multipleTaggingCardType: '',
    multipleTaggingSrDataObj: {}
};

export default function MultipleTaggingReducer(state = initialState, action) {
    switch (action.type) {
        case MultipleTaggingActionType.SET_MULTIPLE_TAGGING_OBJ_SUCCESS:
            return {
                ...state,
                multipleTaggingObject: action.payload,
            };
        case MultipleTaggingActionType.SET_MULTIPLE_TAGGING_CARD_TYPE:
            return {
                ...state,
                multipleTaggingCardType: action.payload,
            };
        case MultipleTaggingActionType.SET_MULTIPLE_TAGGING_SR_CARD_OBJECT:
            return {
                ...state,
                multipleTaggingSrDataObj: action.payload,
            };

        default:
            return state;
    }
};
