import AuthorizationAction from "../actiontype/authorization.actiontype";
import AuthorizationStatus from "../constants/authorization-status.enum";

const initialState = AuthorizationStatus.LOADING

export default function AuthorizationReducer(state = initialState, action) {

    switch (action.type) {
        case AuthorizationAction.USER_AUTHORIZED:
            return AuthorizationStatus.AUTHORIZED
        case AuthorizationAction.USER_UNAUTHORIZED:
            return AuthorizationStatus.UNAUTHORIZED
        case AuthorizationAction.CHECK_AUTH_TOKEN_LOADING:
            return AuthorizationStatus.LOADING
        default:
    }

    return state;
}
