const month_names = ["Jan", "Feb", "Mar",
    "Apr", "May", "Jun",
    "Jul", "Aug", "Sep",
    "Oct", "Nov", "Dec"];

export default class UtilService {
    static getAllQueryParams(allParamsString) {
        var vars = {};
        var parts = allParamsString.replace(/[?&]+([^=&]+)=([^&]*)/gi, function (m, key, value) {
            vars[key] = value;
        });
        return vars;
    }

    static getQueryParamValue(param, allParamsString) {
        var queryParameter = null;
        if (allParamsString.indexOf(param) > -1) {
            queryParameter = this.getAllQueryParams(allParamsString)[param];
            if (queryParameter === '') {
                queryParameter = null;
            }
        }
        return queryParameter;
    }

    static hidePageScroll() {
        // document.body.className += ' ' + 'overflow-hidden';
        document.body.classList.add("overflow-hidden");
    }

    static resetPageScroll() {
        document.body.className = document.body.className.replace("overflow-hidden", "");
    }

    static isPlatformWindows = () => {
        const windowsPlatforms = ["Win32", "Win64", "Windows", "WinCE"];
        const platform = window.navigator.platform;
        if (windowsPlatforms.indexOf(platform) !== -1) {
            return true
        }
        else {
            return false
        }
    }

    static filterArraySize(arr, limit) {
        if (arr && Array.isArray(arr)) {
            if (!limit || isNaN(limit)) {
                limit = arr.length;
            }
            return arr.slice(0, limit);
        }
    }

    static getUniqueValueList(arr, key) {
        const uniqueList = [];
        if (arr && arr.length && key) {
            for (let i = 0; i < arr.length; i++) {
                if (uniqueList.indexOf(arr[i][key]) === -1) {
                    uniqueList.push(arr[i][key]);
                }
            }
        }
        return uniqueList;
    }

    // Return date in YYYY-MM-DD format
    static dateFormateYYYYMMDD(date) {
        const todayTime = new Date(date);
        const month = todayTime.getMonth() + 1;
        const day = todayTime.getDate();
        const year = todayTime.getFullYear();
        return year + "-" + this.pad(month, 2) + "-" + this.pad(day, 2);
    }

    static pad(num, size) {
        let s = num + "";
        while (s.length < size) {
            s = "0" + s;
        }
        return s;
    }

    // Return date in DD-MMM-YYYY format
    static dateFormat_DD_MMM_YYYY(date) {
        if (date) {
            const d = new Date(date);
            // const month_names = ["Jan", "Feb", "Mar",
            //     "Apr", "May", "Jun",
            //     "Jul", "Aug", "Sep",
            //     "Oct", "Nov", "Dec"];

            const day = d.getDate();
            const month_index = d.getMonth();
            const year = d.getFullYear();
            return "" + day + "-" + month_names[month_index] + "-" + year;
        }
        return null;
    }

    // Return date in DD-MMM-YYYY HH:MMformat
    static dateFormat_DD_MMM_YYYY_HH_MM(date) {
        if (date) {
            const d = new Date(date);
            const day = d.getDate();
            const month_index = d.getMonth();
            const year = d.getFullYear();
            const hour = d.getHours();
            const minute = d.getMinutes();
            return "" + day + "-" + month_names[month_index] + "-" + year + " " + this.pad(hour, 2) + ":" + this.pad(minute, 2);
        }
        return null;
    }


}
