import { ScenarioActionType } from "../actiontype/scenario.actiontype";
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";
import CommonAction from "../actiontype/common.actiontype";

const initialState = {
  searchDisabled: true,
  searchScenarioName: '',
  currentScenarioId: null,
  currentStepId: null,
  markedChoiceColor: '',
  scenarioSystemCheckList: [],
  scenarioSystemInfoList: [],
  scenarioStatus: {},
  currentScenarioInfo: {},
  previousScenarioInfo: {},
  breadcrumbChoices: [""],
  scenarioDescription: "",
  choiceList: [],
  crmCode: "",
  updateSrTaskCaseName: "",
  crmButtonText: "CRM Codes",
  scenarioNotes: "",
  notesList: [],
  previousStepsList: [],
  smsTemplate: '',
  isLeapEnable: false,
  autoRequestScenarioNotes: '',
  bookmarkOnCRMCodeClick: null,
  provConfirmInfo: null,
  stepLevelConfig: {
    displayCalender: false
  }
}

export default (state = initialState, action) => {
  switch (action.type) {
    case ScenarioActionType.UPDATE_SEARCH_SCENARIO_TEXT:
      return {
        ...state,
        searchScenarioName: action.payload
      };
    case ScenarioActionType.UPDATE_CURRENT_SCENARIO_ID:
      return {
        ...state,
        currentScenarioId: action.payload,
        bookmarkOnCRMCodeClick: null,
        provConfirmInfo: null
      };
    case ScenarioActionType.SAVE_BOOKMARK_FOR_CRM_CODE_CLICK:
      return {
        ...state,
        bookmarkOnCRMCodeClick: action.payload
      };
    case ScenarioActionType.UPDATE_CURRENT_STEP_ID:
      return {
        ...state,
        currentStepId: action.payload
      }
    case ScenarioActionType.UPDATE_CURRENT_SCENARIO_INFO:
      return {
        ...state,
        previousScenarioInfo: state.currentScenarioInfo,
        currentScenarioInfo: action.payload.result,
        scenarioStatus: action.payload.status
      };
    case ScenarioActionType.UPDATE_PREVIOUS_SCENARIO_INFO:
      return {
        ...state,
        previousScenarioInfo: action.payload
      };
    case ScenarioActionType.UPDATE_SYSTEM_CHECK_COLOR:
      return {
        ...state,
        markedChoiceColor: action.payload
      };
    case ScenarioActionType.UPDATE_SYSTEM_CHECKS:
      return {
        ...state,
        scenarioSystemCheckList: action.payload
      };
    case ScenarioActionType.UPDATE_SYSTEM_INFO:
      return {
        ...state,
        scenarioSystemInfoList: action.payload
      };
    case ScenarioActionType.UPDATE_BREADCRUMB:
      return {
        ...state,
        breadcrumbChoices: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_DESCRIPTION:
      return {
        ...state,
        scenarioDescription: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_CHOICE:
      return {
        ...state,
        choiceList: action.payload
      };
    case ScenarioActionType.UPDATE_CRM_CODE:
      return {
        ...state,
        crmCode: action.payload
      };
    case ScenarioActionType.UPDATE_TASK_CASE_NAME:
      return {
        ...state,
        updateSrTaskCaseName: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_NOTES_FOR_AUTO_REQUEST:
      return {
        ...state,
        autoRequestScenarioNotes: action.payload
      };
    case ScenarioActionType.UPDATE_CRM_BUTTON_TEXT:
      return {
        ...state,
        crmButtonText: action.payload
      }
    case ScenarioActionType.UPDATE_SCENARIO_NOTES:
      return {
        ...state,
        scenarioNotes: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_NOTES_LIST:
      return {
        ...state,
        notesList: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_CHOICE_SMS_TEMPLATE:
      return {
        ...state,
        smsTemplate: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_STEP_HISTORY:
      return {
        ...state,
        previousStepsList: action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_DFF_INFO:
      return {
        ...state,
        ...action.payload
      };
    case ScenarioActionType.UPDATE_SCENARIO_STEP_CONFIGURATION:
      return {
        ...state,
        stepLevelConfig: action.payload
      };
    case ScenarioActionType.UPDATE_LEAP_ENABLE_FLAG:
      return {
        ...state,
        isLeapEnable: action.payload
      };
    case ScenarioActionType.DISABLE_SCENARIO_SEARCH:
      return {
        ...state,
        searchDisabled: true
      }
    case ScenarioActionType.ENABLE_SCENARIO_SEARCH:
      return {
        ...state,
        searchDisabled: false
      }
    case ScenarioActionType.SAVE_PROV_CONFIRM_INFO:
      return {
        ...state,
        provConfirmInfo: action.payload
      }
    case ScenarioActionType.RESET_SCENARIO_VALUES:
      return {
        ...state,
        searchScenarioName: '',
        scenarioSystemCheckList: [],
        scenarioSystemInfoList: [],
        breadcrumbChoices: [""],
        scenarioDescription: "",
        choiceList: [],
        crmCode: "",
        scenarioNotes: "",
        notesList: [],
        previousStepsList: [],
        smsTemplate: '',
        isLeapEnable: false,
        autoRequestScenarioNotes: '',
        markedChoiceColor: '',
        bookmarkOnCRMCodeClick: null,
        provConfirmInfo: null
      };
    case CommonAction.RESET_STATE:
    case CustomerInfoActionType.RESET_CUSTOMER:
      return {
        ...state,
        ...initialState
      };
    default:
      return state;
  }
};
