import {
  DFFActionType
} from "../actiontype/dff.actiontype";
import {
  CustomerInfoActionType
} from "../actiontype/customer-info.actiontype";
import CommonAction from "../actiontype/common.actiontype";

const initialState = {
  dffInfo: [],
  dffNameValuePairs: {},
  dffNotes: "",
  dffSource: null,
  claimDFFInfo: [],
  claimDFFNameValuePairs: {}
}

export default (state = initialState, action) => {
  switch (action.type) {
    case DFFActionType.UPDATE_SCENARIO_DFF_INFO:
      return {
        ...state,
        ...action.payload
      };
    case DFFActionType.UPDATE_SCENARIO_DFF_FIELD_VALUES:
      const id = action.payload.id;
      return {
        ...state,
        dffNameValuePairs: {
          ...state.dffNameValuePairs,
          [id]: {
            ...state.dffNameValuePairs[id],
            value: action.payload.value
          }
        }
      };
    case DFFActionType.UPDATE_SCENARIO_CLAIM_DFF_FIELD_VALUES:
      const key = action.payload.id;
      return {
        ...state,
        claimDFFNameValuePairs: {
          ...state.claimDFFNameValuePairs,
          [key]: action.payload.value
        }
      };
    case DFFActionType.UPDATE_DFF_NOTES:
      return {
        ...state,
        dffNotes: action.payload
      };
    case DFFActionType.UPDATE_DFF_SOURCE:
      return {
        ...state,
        dffSource: action.payload
      };
    case DFFActionType.UPDATE_SCENARIO_CLAIM_DFF_INFO:
      return {
        ...state,
        ...action.payload
      };
    case CommonAction.RESET_STATE:
    case CustomerInfoActionType.RESET_CUSTOMER:
      return {
        ...state,
        ...initialState
      };
    default:
      return state;
  }
};
