import { AppDialogActionType } from "../actiontype/app-dialog.actiontype";
import { AlertMessageDialogActionType } from "../actiontype/alert-message-dialog.actiontype";

export function openAlertMessageDialog(message) {
    return { type: AlertMessageDialogActionType.OPEN_ALERT_MESSAGE_DIALOG, payload: message }
}
export function closeAlertMessageDialog() {
    return { type: AlertMessageDialogActionType.CLOSE_ALERT_MESSAGE_DIALOG }
}
