import { HandsetDetailsActionType } from '../actiontype/handset-details-card.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';
const initialState = {
    HandsetDetailsCardStatus: '',
    HandsetDetailsCardDataResponse: {},
    handsetScenarioCardLinksData: {}
};

export default function HandsetDetailsCardReducer(state = initialState, action) {
    switch (action.type) {
        case HandsetDetailsActionType.HANDSET_DETAILS_CARD_REQUEST:
            return {
                ...state,
                HandsetDetailsCardStatus: "LOADING",
                HandsetDetailsCardDataResponse: action.payload
            };
        case HandsetDetailsActionType.HANDSET_DETAILS_CARD_SUCCESS:
            return {
                ...state,
                HandsetDetailsCardStatus: true,
                HandsetDetailsCardDataResponse: action.payload
            };
        case HandsetDetailsActionType.HANDSET_DETAILS_CARD_FAILURE:
            return {
                ...state,
                HandsetDetailsCardStatus: false,
            };
        case HandsetDetailsActionType.SET_HANDSET_SCENARIO_CARD_LINKS:
            return {
                ...state,
                handsetScenarioCardLinksData: action.payload
            };

        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }
        default:
            return state
    }

}
