import {
    TaskUpdateActionType
  } from "../actiontype/task-update.actiontype";
  import {
    CustomerInfoActionType
  } from "../actiontype/customer-info.actiontype";
  import CommonAction from "../actiontype/common.actiontype";

  const initialState = {
    taskfieldsInfo: [],
    taskFieldsNameValuePairs: {},
    updateSrFieldInfo: {},
    taskFieldSrNumber: '',
    updateSrFieldValue: '',
    srTaskUpdateCaseNames: [],
    srTaskUpdateCaseName: '',
    srTaskUpdateSrNumber: '',
    selectedSrTaskDetails: []
  }

  export default (state = initialState, action) => {
    switch (action.type) {
      case TaskUpdateActionType.UPDATE_SCENARIO_TASK_INFO:
        return {
          ...state,
          ...action.payload
        };
      case TaskUpdateActionType.UPDATE_SR_FIELD_INFO:
        return {
          ...state,
          updateSrFieldInfo: action.payload
        }
      case TaskUpdateActionType.UPDATE_SCENARIO_TASK_FIELD_VALUES:
        const id = action.payload.id;
        return {
            ...state,
            taskFieldsNameValuePairs: {
              ...state.taskFieldsNameValuePairs,
              [id]: {
                ...state.taskFieldsNameValuePairs[id],
                value: action.payload.value
              }
            }
          };
      case TaskUpdateActionType.UPDATE_SCENARIO_TASK_SR_NUMBER:
        return {
            ...state,
            taskFieldSrNumber: action.payload,
        };
      case TaskUpdateActionType.UPDATE_SR_FIELD_VALUE:
          return {
            ...state,
            updateSrFieldValue: action.payload
          };

      case TaskUpdateActionType.UPDATE_SR_TASK_UPDATE_CASES:
          return {
            ...state,
            srTaskUpdateCaseNames: action.payload,
            srTaskUpdateCaseName: action.payload[0]
          };

      case TaskUpdateActionType.UPDATE_SR_TASK_UPDATE_CASE_NAME:
        return {
          ...state,
          srTaskUpdateCaseName: action.payload
        };

      case TaskUpdateActionType.UPDATE_SR_TASK_UPDATE_SR_NUMBER:
        return {
          ...state,
          srTaskUpdateSrNumber: action.payload
        };
      case TaskUpdateActionType.UPDATE_SELECTED_SR_TASK_DETAILS:
        return {
          ...state,
          selectedSrTaskDetails: action.payload
        };

      case CommonAction.RESET_STATE:
      case CustomerInfoActionType.RESET_CUSTOMER:
        return {
          ...state,
          ...initialState
        };
      default:
        return state;
    }
  };
