const styles = theme => ({
    root: {
        '& tbody': {
            display: 'block',
            maxHeight: 'calc(100vh - 350px);',
            overflow: 'auto'
        },
        '& thead, tbody tr': {
            display: 'table',
            width: '100%',
            tableLayout: 'fixed'
        },
        '& th, td': {
            padding: 10,
            fontSize: 14
        },
        '& th': {
            color: '#000',
            fontWeight: 'bold',
        },
        '& td': {
            color: '#666',
        },
        '& .not-found': {
            fontSize: 20,
            color: '#ccc',
            textAlign: 'center',
            padding: 50
        }
    },
    cell: {
        position: 'absolute',
        top: 5,
        right: 0,
        bottom: 0,
        left: 0,
        boxSizing: 'border-box',
        display: 'block',
        padding: 8,
        width: '100%'
    },
    cellOverflow: {
        boxSizing: 'border-box',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        whiteSpace: 'nowrap'
    },
    formControl: {
        width: '100%'
    },
    details: {
        padding: '10px 0px',
        textAlign: 'justify'
    },
    cetagoryFilter: {
        position: 'absolute',
        top: 0,
        right: 0,
        width: '20%'
    },
    visuallyHidden: {
        border: 0,
        clip: 'rect(0 0 0 0)',
        height: 1,
        margin: -1,
        overflow: 'hidden',
        padding: 0,
        position: 'absolute',
        top: 20,
        width: 1,
    }
});
export default styles;
