import React, { Component } from 'react';
import CustomizedTabs from '../../../common/customized-tabs/customized-tabs.dumb';
import { withStyles } from '@material-ui/core/styles';
import { switchSRNotesTab } from '../../../../actions/tabs.action';
import { connect } from 'react-redux';
import styles from './sr-notes-dialog.style';

const tabOptions = {
    // scrollable: false,
}

class SRNotesDialog extends Component {

    handleChange = (event) => {
        const id = event.currentTarget.id;
        this.props.switchSRNotesTab(id);
    };

    getTabContent(key) {
        const { srNotesTabList } = this.props;
        if (key) {
            const tabObj = srNotesTabList[key];
            const obj = {
                index: Object.keys(srNotesTabList).indexOf(key),
                content: tabObj.component
            }
            return obj;
        }
        return {};
    }

    render() {
        const { classes, srNotesActiveTab, srNotesTabList } = this.props;
        const { index, content } = this.getTabContent(srNotesActiveTab);
        // const tabContentHeight = '500px'
        return (
            <div className={classes.root + " system-check-wrapper mb-10"}>
                <CustomizedTabs value={index} tabOptions={tabOptions} handleChange={this.handleChange.bind(this)} tabList={srNotesTabList} content={content}></CustomizedTabs>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        srNotesActiveTab: state.tab.srNotesActiveTab,
        srNotesTabList: state.tab.srNotesTabList
    };
};

const mapDispatchToProps = dispatch => ({
    switchSRNotesTab: index => dispatch(switchSRNotesTab(index))
});

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(SRNotesDialog));
