import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';

import EncodeDecodeType from '../actiontype/encode-decode-type';
class LoginService {

    static fetchLoginApi(logindata) {
        const config = {
            headers: {
                'Content-Type': "application/x-www-form-urlencoded",
            }
        };
        const data = "grant_type=password&username=" + logindata["username"] + "&password=" + btoa(logindata["password"]) + "&client_id=b2b&agentId=" + logindata["username"];
        var url = Config.authPath + "oauth/token";
        return CustomHttpService.post(url, data, config, EncodeDecodeType.DEFAULT);
    }
}

export default LoginService;
