import { PredictionActionType } from '../actiontype/prediction.actiontype';
import CommonAction from "../actiontype/common.actiontype";
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";

const initialState = {
  isPredictionAvailable: false,
  displayPredictionWindow: false,
  predictionStatusCode: null,
  predictionScenarioP1: null,
  predictionScenarioP2: null,
  predictionScenarioP3: null,
  predictionScenarioP1Desc: '',
  predictionScenarioP2Desc: '',
  predictionScenarioP3Desc: '',
  predictionScenarioP1CRMCode: null,
  predictionScenarioP2CRMCode: null,
  displayP1Desc: false,
  displayP2Desc: false,
  displaySuggestion: false,
  ftrResponseObj: null,
  srResponseObj: null,
  dffResponseObj: null,
  suggestionTitle: null,
  predictionScenarioP3DescArr: [],
  predictionScenarioP1ChoiceList: [],
  predictionScenarioP2ChoiceList: [],
  suggestionChoiceBtnList: [],
  predictionData: null,
  predictionTagScenario: {}
};

export default function loginReducer(state = initialState, action) {
  switch (action.type) {
    case PredictionActionType.FETCH_PREDICTION_SCENARIO:
      return {
        ...state,
        predictionData: action.payload
      };
    case PredictionActionType.PREDICTION_FOUND_FLAG:
      return {
        ...state,
        isPredictionAvailable: action.payload
      };
    case PredictionActionType.DISPLAY_PREDICTION_WINDOW_FLAG:
      return {
        ...state,
        displayPredictionWindow: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_STATUS:
      return {
        ...state,
        predictionStatusCode: action.payload
      };
    case PredictionActionType.DISPLAY_P1_DESCRIPTION:
      return {
        ...state,
        displayP1Desc: action.payload
      };
    case PredictionActionType.DISPLAY_P2_DESCRIPTION:
      return {
        ...state,
        displayP2Desc: action.payload
      };
    case PredictionActionType.UPDATE_P1_SCENARIO:
      return {
        ...state,
        predictionScenarioP1: action.payload
      };
    case PredictionActionType.UPDATE_P2_SCENARIO:
      return {
        ...state,
        predictionScenarioP2: action.payload
      };
    case PredictionActionType.UPDATE_P3_SCENARIO:
      return {
        ...state,
        predictionScenarioP3: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_TAG_SCENARIO:
      return {
        ...state,
        predictionTagScenario: action.payload
      };
    case PredictionActionType.UPDATE_P1_CRM_CODE:
      return {
        ...state,
        predictionScenarioP1CRMCode: action.payload
      };
    case PredictionActionType.UPDATE_P2_CRM_CODE:
      return {
        ...state,
        predictionScenarioP2CRMCode: action.payload
      };
    case PredictionActionType.UPDATE_P1_SCENARIO_DESCRIPTION:
      return {
        ...state,
        predictionScenarioP1Desc: action.payload
      };
    case PredictionActionType.UPDATE_P2_SCENARIO_DESCRIPTION:
      return {
        ...state,
        predictionScenarioP2Desc: action.payload
      };
    case PredictionActionType.UPDATE_P3_SCENARIO_DESCRIPTION:
      return {
        ...state,
        predictionScenarioP3Desc: action.payload
      };
    case PredictionActionType.UPDATE_P1_SCENARIO_CHOICE:
      return {
        ...state,
        predictionScenarioP1ChoiceList: action.payload
      };
    case PredictionActionType.UPDATE_P2_SCENARIO_CHOICE:
      return {
        ...state,
        predictionScenarioP2ChoiceList: action.payload
      };
    case PredictionActionType.UPDATE_SUGGESTION_CHOICE:
      return {
        ...state,
        suggestionChoiceBtnList: action.payload
      };
    case PredictionActionType.DISPLAY_SUGGESTION_FLAG:
      return {
        ...state,
        displaySuggestion: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_FTR_RESPONSE:
      return {
        ...state,
        ftrResponseObj: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_SR_RESPONSE:
      return {
        ...state,
        srResponseObj: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_DFF_RESPONSE:
      return {
        ...state,
        dffResponseObj: action.payload
      };
    case PredictionActionType.UPDATE_PREDICTION_SUGGESTION_TITLE:
      return {
        ...state,
        suggestionTitle: action.payload
      };
    case CommonAction.RESET_STATE:
    case CustomerInfoActionType.RESET_CUSTOMER:
      return {
        ...state, ...initialState
      }
    default:
      return state
  }
}
