import React, { Component } from 'react';
import { connect } from 'react-redux';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle'
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import '../../../../index.css';
import { closeSRHistoryDialog } from '../../../../actions/dialog.action';
import styles from '../dialog-style';
import PreviousSRHistoryTab from '../../../common/previous-interactions/previous-interactions-sr.smart';
import PreviousFTRHistoryTab from '../../../common/previous-interactions/previous-interactions-ftr.smart';
// import CustomizedTabs from '../../../common/customized-tabs/customized-tabs.dumb';
import './previous-history-dialog.style';

const tabList = [
    {
        label: "SR",
        component: <PreviousSRHistoryTab />
    },
    {
        label: "FTR",
        component: <PreviousFTRHistoryTab />
    }
]
class PreviousSRHistoryDialog extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 0,
            content: tabList[0]['component']
        };
        this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleChange = (event, value) => {
        this.setState({ value, content: tabList[value]['component'] });
    };

    handleCloseModal = (event) => {
        this.props.closeSRHistoryDialog();
    }

    render() {
        const previousInteractionsDialogTitle = this.props.previousInteractionsDialogTitle;
        const { classes } = this.props;
        const previousInteractionsDialogStatus = this.props.previousInteractionsDialogStatus;

        return (
            <React.Fragment>
                <Dialog
                    open={previousInteractionsDialogStatus}
                    onClose={this.handleCloseModal}
                    maxWidth={`lg`}
                    fullWidth={true}
                    aria-labelledby="form-dialog-title"
                >
                    <DialogTitle id="form-dialog-title">
                        {previousInteractionsDialogTitle}
                        <IconButton aria-label="Close" className={classes.closeButton} onClick={this.handleCloseModal}>
                            <CloseIcon />
                        </IconButton>
                    </DialogTitle>
                    <DialogContent>
                        <div>
                            <PreviousSRHistoryTab activeTab={this.props.previousInteractionsActiveTab} />
                            {/* <CustomizedTabs value={value} classes={classes} handleChange={this.handleChange.bind(this)} tabList={tabList} content={content}></CustomizedTabs> */}
                        </div>
                    </DialogContent>
                </Dialog>
            </React.Fragment>

        )
    }
}
const mapDispatchToProps = dispatch => ({
    closeSRHistoryDialog: () => dispatch(closeSRHistoryDialog())
});
const mapStateToProps = state => {
    return {
        previousInteractionsDialogStatus: state.dialogInfo.previousInteractionsDialogStatus,
        previousInteractionsDialogTitle: state.dialogInfo.previousInteractionsDialogTitle,
        previousInteractionsActiveTab: state.dialogInfo.previousInteractionsActiveTab,
    };
}

PreviousSRHistoryDialog.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default connect(
    mapStateToProps, mapDispatchToProps
)(withStyles(styles)(PreviousSRHistoryDialog));
