import { FTRActionType } from "../actiontype/ftr.actiontype";
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";

const initialState = {
  successMessage: null,
  interactionId: null,
  faultTrace: null,
  message: null
};

export default (state = initialState, action) => {
  switch (action.type) {
    case FTRActionType.SCENARIO_FTR_REQUEST:
      return {
        ...state,
        ...action.payload
      };
    case FTRActionType.CALL_TRANSFER_FTR_REQUEST:
      return {
        ...state,
        ...action.payload
      };
    case CustomerInfoActionType.RESET_CUSTOMER:
      return {
        ...state,
        ...initialState
      };
    default:
      return state;
  }
};
