import React, { Component } from 'react';
import styles from '../dialog-style';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import { SnackbarMessages } from '../../../../constants/snackbar-messages';
export class ArcMessageDialog extends Component {
    render() {
        const { arcDialogSubmittedResponse } = this.props;
        return (
            <div>
                {
                    arcDialogSubmittedResponse && arcDialogSubmittedResponse['tagId'] &&
                    <p> {SnackbarMessages.ARC_ISSUE_SUCCESS_MSG} {arcDialogSubmittedResponse['tagId']} </p>
                }
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => ({
});
const mapStateToProps = state => {
    return {
        arcDialogSubmittedResponse: state.arcIssueInfo.submitArcIssueDialogApiResponse,
    }
}

export default connect(
    mapStateToProps, mapDispatchToProps
)(withStyles(styles)(ArcMessageDialog));
