import LeftNavActionType from "../actiontype/left-nav.actiontype";
import PowerSettingsNew from '@material-ui/icons/PowerSettingsNew';
import React from 'react'
import { fetchBlankCallData } from '../actions/blank-call.action';
import { openCallDropDialog, openCallTransferDialog, openFeedbackDialog, openArcIssueDialog, openSelfHelpDialog } from '../actions/dialog.action';
import { handleLogout } from "../actions/login.action"
import RingVolume from '@material-ui/icons/RingVolume';
import PhoneForwarded from '@material-ui/icons/PhoneForwarded';
import HeadsetMic from '@material-ui/icons/HeadsetMic';
import AutoPredict from '../assets/images/auto-predict-icon.svg';
import ShoppingBag from '../assets/images/shopping-bag.svg';
import CallDropIcon from '../assets/images/calldrop.svg';
import SelfHelpIcon from '../assets/images/self-help.svg'
import LeftNavMenuKey from '../constants/left-nav-menu.enum'
import CommonActionType from "../actiontype/common.actiontype";
import ArcIssue from '../assets/images/arc-issues-icon.svg';
import { CustomerInfoActionType } from "../actiontype/customer-info.actiontype";
import { toggleUWQWindowDisplay } from "../actions/universal-work-queue.action";

const initialState = {
    menuItems: {
        [LeftNavMenuKey.TRANSFER]: {
            title: 'TRANSFER',
            icon: <PhoneForwarded className="sidebar-icon" />,
            type: 'actionClick',
            disabled: true,
            onClickAction: openCallTransferDialog
        },
        [LeftNavMenuKey.CALL_DROP]: {
            title: 'CALL DROP',
            icon: <img src={CallDropIcon} className="sidebar-icon" alt="logo" />,
            type: 'actionClick',
            disabled: true,
            onClickAction: openCallDropDialog
        },
        [LeftNavMenuKey.FEEDBACK]: {
            title: 'FEEDBACK',
            icon: <HeadsetMic className="sidebar-icon" />,
            type: 'actionClick',
            disabled: true,
            onClickAction: openFeedbackDialog
        },
        [LeftNavMenuKey.UNIVERSAL_WORK_QUEUE]: {
            title: 'UWQ',
            icon: <img src={ShoppingBag} className="sidebar-icon" alt="logo" />,
            type: 'actionClick',
            disabled: false,
            onClickAction: toggleUWQWindowDisplay
        },
        [LeftNavMenuKey.LOGOUT]: {
            title: 'LOGOUT',
            icon: <PowerSettingsNew className="sidebar-icon" />,
            type: 'actionClick',
            onClickAction: handleLogout
        }
    }

};

function leftNavReducer(state = initialState, action) {
    switch (action.type) {
        //-- GENERAL --- //
        case LeftNavActionType.ENABLE_LEFT_NAV_ITEM:
            return { ...state, menuItems: { ...state.menuItems, [action.payload]: { ...state.menuItems[action.payload], disabled: false } } }
        case LeftNavActionType.DISABLE_LEFT_NAV_ITEM:
            return { ...state, menuItems: { ...state.menuItems, [action.payload]: { ...state.menuItems[action.payload], disabled: true } } }

        //-- TRANSFER --- //
        case LeftNavActionType.ENABLE_TRANSFER_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.TRANSFER]: { ...state.menuItems[LeftNavMenuKey.TRANSFER], disabled: false } } }
        case LeftNavActionType.DISABLE_TRANSFER_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.TRANSFER]: { ...state.menuItems[LeftNavMenuKey.TRANSFER], disabled: true } } }
        //-- CALL DROP --- //
        case LeftNavActionType.ENABLE_CALL_DROP_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.CALL_DROP]: { ...state.menuItems[LeftNavMenuKey.CALL_DROP], disabled: false } } }
        case LeftNavActionType.DISABLE_CALL_DROP_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.CALL_DROP]: { ...state.menuItems[LeftNavMenuKey.CALL_DROP], disabled: true } } }
        //-- FEEDBACK --- //
        case LeftNavActionType.ENABLE_FEEDBACK_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.FEEDBACK]: { ...state.menuItems[LeftNavMenuKey.FEEDBACK], disabled: false } } }
        case LeftNavActionType.DISABLE_FEEDBACK_CLICK:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.FEEDBACK]: { ...state.menuItems[LeftNavMenuKey.FEEDBACK], disabled: true } } }

        case LeftNavActionType.ENABLE_UNIVERSAL_WORK_QUEUE:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.UNIVERSAL_WORK_QUEUE]: { ...state.menuItems[LeftNavMenuKey.UNIVERSAL_WORK_QUEUE], disabled: false } } }
        case LeftNavActionType.DISABLE_UNIVERSAL_WORK_QUEUE:
            return { ...state, menuItems: { ...state.menuItems, [LeftNavMenuKey.UNIVERSAL_WORK_QUEUE]: { ...state.menuItems[LeftNavMenuKey.UNIVERSAL_WORK_QUEUE], disabled: true } } }


        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return { ...state, ...initialState }

        default:
    }       return { ...state }
}


export default leftNavReducer;
