import React from 'react';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import styles from './system-info.style';

const SystemInfo = (props) => {
    const { classes, scenarioSystemInfoList } = props;
    return (
        <div className={classes.constainer + " padding-10"}>
            {scenarioSystemInfoList.map((item, index) => {
                return (
                <div className="mb-10 mt-5" key={index}>
                <p className="fs-12 color-grey">{item['key']}</p>
                <p className="fs-16 color-black">{item['description']}</p>
                </div>
                );
            })}
        </div>
    );
};

SystemInfo.propTypes = {
    classes: PropTypes.object.isRequired,
  };

const mapStateToProps = state => {
    return {
        scenarioSystemInfoList: state.scenario.scenarioSystemInfoList
    };
};

export default connect(mapStateToProps, null)(withStyles(styles)(SystemInfo));
