import React from 'react';
import FilledInput from '@material-ui/core/FilledInput';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import CalendarPNG from '../../../assets/images/calendar.png';
import styles from './bill-comparator-calendar.style';
import { withStyles } from '@material-ui/core/styles';

const BillComparatorCalendar = (props) => {
    const { classes, selectedMonth, billCycle, billCycleHandler, name, billComparisonDate, title, classProp } = props;
    return (
        <React.Fragment>
            <FormControl variant="filled" className={classes.formControl + " " + classProp}>
                <img className={classes.calendarImg} src={CalendarPNG} alt="" />
                <InputLabel className={classes.label} htmlFor="filled-bill-cycle">
                    {title} 
                    <span className={billCycle ? '' : 'hidden'}>&nbsp;({billCycle})</span>
                </InputLabel>
                <Select
                    value={selectedMonth}
                    onChange={billCycleHandler}
                    input={<FilledInput name={name} className={classes.inputText} />}
                >
                    <MenuItem value="" disabled={true}>
                    <em>Select Month</em>
                    </MenuItem>
                    {billComparisonDate.map((item, index) => (
                        <MenuItem key={index} value={item}>{item.displayName}</MenuItem>
                    ))}
                </Select>
            </FormControl>
        </React.Fragment>
    );
};

export default withStyles(styles)(BillComparatorCalendar);
