import React, { Component } from 'react';
import styles from './sr-message-dialog.style';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import SuccessIcon from '../../../../assets/images/success-icon.svg';
import Typography from '@material-ui/core/Typography';
import Tooltips from '../../../common/tooltip/tooltip.dumb';

class SRMessageDialog extends Component {
    render() {
        const { classes, successMessage, interactionId, faultTraceObj, message, sla } = this.props;
        const { soaFaultOriginator, faultTrace, soaFaultCode } = faultTraceObj || {}

        return (
            <div className="pt-30 pb-20">
                <p className={'text-center mb-30' + (interactionId ? '' : ' hidden')}>
                    <img src={SuccessIcon} alt="" width="90" />
                </p>

                <p className={'fs-18 text-center color-707070' + (interactionId ? '' : ' hidden')}>{successMessage}</p>
                <p className={'fs-18 text-center color-707070' + (!interactionId ? '' : ' hidden')}>{message}</p>

                <div className={classes.srInteraction + (interactionId ? '' : ' hidden')}>
                    <div className="pull-left width-30 fs-18 pt-20 pb-20 text-center color-707070">SR Number</div>
                    <div className="pull-left width- fs-18 pt-20 pb-20 pl-20 text-center color-black border-left">{interactionId}</div>
                </div>
                <div className={classes.srInteraction + (sla ? '' : ' hidden')}>
                    <div className="pull-left width-30 fs-18 pt-20 pb-20 text-center color-707070">SLA</div>
                    <div className="pull-left width-65 fs-18 pt-20 pb-20 pl-20 text-center color-black border-left">{sla}</div>
                </div>
                <div className={classes.srInteraction + (faultTraceObj ? '' : ' hidden')}>
                    <div className="pull-left width-30 fs-16 pt-20 pb-20 pr-20 pl-20 text-center color-707070 border-right">Fault Originator</div>
                    <div className="pull-left width-70 fs-16 pt-20 pb-20 pr-20 pl-20 color-black">{soaFaultOriginator}</div>
                </div>
                <div className={classes.srInteraction + (faultTraceObj ? '' : ' hidden')}>
                    <div className="pull-left width-30 fs-16 pt-20 pb-20 pr-20 pl-20 text-center color-707070 border-right">Fault Trace</div>
                    <div className="pull-left width-70 fs-16 pt-20 pb-20 pr-20 pl-20 color-black">
                        <Tooltips tooltipText={faultTrace}>
                            <Typography variant="inherit" noWrap>
                                {faultTrace}
                            </Typography>
                        </Tooltips>
                    </div>
                </div>
                <div className={classes.srInteraction + (faultTraceObj ? '' : ' hidden')}>
                    <div className="pull-left width-30 fs-16 pt-20 pb-20 pr-20 pl-20 text-center color-707070 border-right">Fault Code</div>
                    <div className="pull-left width-70 fs-16 pt-20 pb-20 pr-20 pl-20 color-black">{soaFaultCode}</div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        successMessage: state.sr.successMessage,
        interactionId: state.sr.interactionId,
        faultTraceObj: state.sr.faultTrace,
        message: state.sr.message,
        sla: state.sr.sla
    };
};

export default connect(mapStateToProps, null)(withStyles(styles)(SRMessageDialog));
