import React, { Component } from 'react';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import './login.style.css';
import { fetchLoginData } from '../../actions/login.action';
import { withRouter } from 'react-router'
import { HashRouter as Router, Route, Redirect } from 'react-router-dom';

class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.state = { username: '', password: '' };
        this.onLoginHandler = this.onLoginHandler.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleClickShowPassword = this.handleClickShowPassword.bind(this);
    }

    handleClickShowPassword = () => {
        this.setState(state => ({ showPassword: !state.showPassword }));
    };
    handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
    };

    onLoginHandler = (event) => {
        event.preventDefault();
        const { username, password } = this.state;
        let loginObj = {
            username,
            password
        }

    }
    render() {
        const { username, password } = this.state;
        const isEnabled = username.length > 0 && password.length > 0;
        // check for login status 
        if (this.props.loginStatus && this.props.loginStatus === "loginSuccess") {
            const loginSuccessPath = `/home${this.props.location.search}`;
            return (
                <Redirect exact from='/login' to={loginSuccessPath} ></Redirect>
            )
        }
        return (
            <div>

                <form onSubmit={this.onLoginHandler}>
                    <FormControl fullWidth>
                        <TextField
                            label="Username"
                            placeholder="Enter OLM Id"
                            margin="normal"
                            type="text"
                            value={this.state.username}
                            onChange={this.handleChange('username')}
                        />
                    </FormControl>

                </form>
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => ({
    fetchLoginData: data => dispatch(fetchLoginData(data)),
});
const mapStateToProps = state => {
    return {
        loginStatus: state.loginInfo.loginStatus,
        loginApiResponse: state.loginInfo.loginApiResponse
    };
}

export default withRouter(connect(
    mapStateToProps, mapDispatchToProps
)(LoginForm));
