import { UWQActionType } from '../actiontype/universal-work-queue.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';

const initialState = {
    displayTickets: false,
    ticketList: [],
    order: 'asc',
    orderBy: 'calories',
    loadingStatus: null,
    filterObj: {
        status: [],
        customerSegment: []
    },
    filterForm: {
        status: '',
        custSegment: ''
    },
    filterCount: 0
};

export default function UWQReducer(state = initialState, action) {
    switch (action.type) {
        case UWQActionType.TOGGEL_UWQ_TICKET_DISPLAY:
            return {
                ...state,
                displayTickets: action.payload
            };
        case UWQActionType.UPDATE_TICKET_LIST:
            return {
                ...state,
                ticketList: action.payload
            };
        case UWQActionType.UPDATE_TICKET_LIST_ORDER:
            return {
                ...state,
                order: action.payload
            };
        case UWQActionType.UPDATE_UWQ_FILTER_VALUES:
            return {
                ...state,
                filterObj: action.payload
            };
        case UWQActionType.UPDATE_TICKET_LIST_ORDER_BY:
            return {
                ...state,
                orderBy: action.payload
            };
        case UWQActionType.RESET_UWQ_FILTER_FORM:
            return {
                ...state,
                filterForm: { ...initialState.filterForm }
            };
        case UWQActionType.UPDATE_UWQ_FILTER_FORM:
            return {
                ...state,
                filterForm: action.payload
            };
        case UWQActionType.UPDATE_UWQ_API_REQUEST_STATUS:
            return {
                ...state,
                loadingStatus: action.payload
            };
        case UWQActionType.UPDATE_UWQ_FILTER_COUNT:
            return {
                ...state,
                filterCount: action.payload
            };
        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }
        default:
            return state
    }
}
