import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Cached from '@material-ui/icons/Cached';
import TextField from '@material-ui/core/TextField';
import { setCustomerSRNumberInput, setCustomerLSINumberInput, setCustomerAccountNumberInput, setCustomerAccountNumber, setCustomerSRNumber, setCustomerLSINumber, resetCustomerInfoRequest, enableAutoFillLSI, disableAutoFillLSI } from '../../../actions/customer-info.action';
import ExternalLinks from '../external-links/external-links.smart';
import styles from './header.style';
import { enableAllTaggings } from '../../../actions/disabled-tagging.action';
import { resetState } from '../../../actions/common.action'
import { withRouter } from 'react-router'
import { onAccountNumberChange, onSRNumberChange, onLSINumberChange } from "../../../actions/update-customer-data.actions"
import { cardLinksHandler } from '../../../actions/card-links.action';
import { openPreviousInteractionsDialog } from '../../../actions/dialog.action';
import PreviousSRHistoryDialog from '../../shared/dialogs/sr-details-history-dialog/previous-history-dialog.dumb';
import Autosuggest from 'react-autosuggest';
import Paper from '@material-ui/core/Paper';
import MenuItem from '@material-ui/core/MenuItem';
import match from 'autosuggest-highlight/match';
import parse from 'autosuggest-highlight/parse';
import deburr from 'lodash/deburr';
import  AnalyticsService from '../../../services/analytics-service';

class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {
            lsiNumber: "",
            value: "",
            suggestions: this.getSuggestions("")
        }
        this.refreshHandler = this.refreshHandler.bind(this);
        this.props.history.listen((location, action) => {
            if (location.pathname === '/home') {
                if (action !== 'REPLACE') {
                    this.handleRTNParamChange(location);
                }
            }
        });
        this.getSuggestions = this.getSuggestions.bind(this);
        this.getSuggestionValue = this.getSuggestionValue.bind(this);
        this.renderSuggestion = this.renderSuggestion.bind(this);
        this.renderInputComponent = this.renderInputComponent.bind(this);
        this.onSuggestionSelected = this.onSuggestionSelected.bind(this);

    }


    renderInputComponent(inputProps) {
        const { inputRef = () => { }, ref, ...other } = inputProps;
        const { classes } = this.props;
        return (
            <TextField
                label="ENTER LSI NO."
                className={classes.textField}
                margin="normal"
                autoFocus={true}
                fullWidth
                InputProps={{
                    inputRef: node => {
                        ref(node);
                        inputRef(node);
                    }
                }}
                {...other}
            />
        );
    }

    getSuggestions = value => {
        const inputValue = deburr(value.trim()).toLowerCase();
        const inputLength = inputValue.length;
        let count = 0;
        let filteredElements = []
        if (this.props.customerLSIDetails) {
            filteredElements
                = this.props.customerLSIDetails.filter(suggestion => {
                    let keep = count < 5 && suggestion.lsiNumber.toLowerCase().includes(inputValue);
                    if (inputValue === "") {
                        keep = count < 5;
                    }
                    if (keep) {
                        count += 1;
                    }

                    return keep;
                });
            if (count === 1 && filteredElements[0].lsiNumber === inputValue && inputValue === this.props.customerLSINumber) {
                return [];
            }
        }
        return filteredElements;
    };

    getSuggestionValue = suggestion => suggestion.lsiNumber;

    renderSuggestion(suggestion, { query, isHighlighted }) {
        const matches = match(suggestion.lsiNumber, query);
        const parts = parse(suggestion.lsiNumber, matches);
        return (
            <MenuItem selected={isHighlighted} component="div">
                <div>
                    {parts.map(part => (
                        <span key={part.text} style={{ fontWeight: part.highlight ? 500 : 400 }}>
                            {part.text}
                        </span>
                    ))}
                </div>
            </MenuItem>
        );
    }

    onChangeAccountNumber = (event) => {
        let value = event.target.value;
        if (this.validateNumber(value)) {
            this.props.setCustomerAccountNumberInput(value);
        }
    }

    onClickSRNumber = (event) => {
        // AnalyticsService.trackEvent({
        //     eVar8: "b2b|dt|homepage",
        //     eVar9: "b2b|dt|homepage|sr_number",
        // })
    }

    onClickAccountNumber = (event) => {
        // AnalyticsService.trackEvent({
        //     eVar8: "b2b|dt|homepage",
        //     eVar9: "b2b|dt|homepage|account_number",
        // })
    }

    onClickLsiNumber = (event) => {
        // AnalyticsService.trackEvent({
        //     eVar8: "b2b|dt|homepage",
        //     eVar9: "b2b|dt|homepage|lsi_number",
        //   })
    }

    onChangeSRNumber = (event) => {
        let value = event.target.value;
        if (this.validateNumber(value)) {
            this.props.setCustomerSRNumberInput(value);
        }
    }

    onChangeLSINumber = (event, value) => {
        // if (this.validateNumber(event.target.value)) {
        //     this.props.setCustomerLSINumberInput(event.target.value);
        // }
        this.props.setCustomerLSINumberInput(event.target.value);
    }

    onSubmitAccountNumber = (event) => {
        if (event.key === 'Enter') {
            AnalyticsService.trackEvent({
                eVar9: "b2b|dt|homepage|account_number|enter",
                eVar8: "b2b|dt|homepage",
                prop22: event.target.value,
            })
            this.props.onAccountNumberChange(event.target.value);
        } else {
            this.onChangeAccountNumber(event);
        }
    }

    onSubmitSRNumber = (event) => {
        if (event.key === 'Enter') {
            AnalyticsService.trackEvent({
                eVar9: "b2b|dt|homepage|sr_number|enter",
                eVar8: "b2b|dt|homepage",
                prop23: event.target.value,
            })
            this.props.onSRNumberChange(event.target.value);
        } else {
            this.onChangeSRNumber(event);
        }
    }

    onSubmitLSINumber = (event) => {
        if (event.key === 'Enter') {
            AnalyticsService.trackEvent({
                eVar9: "b2b|dt|homepage|lsi_number|enter",
                eVar8: "b2b|dt|homepage",
                prop21: event.target.value,
            })
            this.props.onLSINumberChange(event.target.value);
        } else {
            this.onChangeLSINumber(event);
        }
    }
    validateNumber(value) {
        const regexForOnlyNumber = /^[0-9]*$/;
        if (value.match(regexForOnlyNumber)) {
            return true;
        }
        return false;
    }

    getRepeatCount(customerInfo) {
        const obj = {
            repeatCount: "",
            repeatCountColor: ""
        }
        if (customerInfo && customerInfo.repeatCountCard) {
            obj.repeatCount = customerInfo.repeatCountCard.count;
            obj.repeatCountColor = customerInfo.repeatCountCard.colour;
        }
        return obj;
    }

    getEmailId(customerInfo) {
        let emaiId = "";
        if (customerInfo && customerInfo.emails && customerInfo.emails.cardContent && customerInfo.emails.cardContent.primaryEmail) {
            emaiId = customerInfo.emails.cardContent.primaryEmail
        } else if (customerInfo && customerInfo.emails && customerInfo.emails.cardContent && customerInfo.emails.cardContent.secondaryEmail) {
            emaiId = customerInfo.emails.cardContent.secondaryEmail
        } else if (this.props.isCustomerFound) {
            emaiId = "NA";
        }
        return emaiId;
    }

    getCustomerAddress(customerInfo) {
        let address = "";
        if (customerInfo && customerInfo.address && customerInfo.address.correspondence) {
            address = customerInfo.address.correspondence
        } else if (customerInfo && customerInfo.address && customerInfo.address.local) {
            address = customerInfo.address.local
        }
        return address;
    }

    refreshHandler() {
        AnalyticsService.trackEvent({
            eVar9: "b2b|dt|homepage|apprefresh|click",
            eVar8: "b2b|dt|homepage",
            prop21: null,
            prop22: null,
            prop23: null,
            prop24: null,
            prop25: null,
            prop26: null,
            prop27: null,
            prop28: null,
            prop29: null,
            prop30: null,
            prop31: null,
            prop32: null,
        })
        this.props.resetState();
    }

    handleChange = (value) => {
        this.setState({ lsiNumber: value })
        let lsiNumber = value;
        this.props.onLSINumberChange(lsiNumber);
    }

    onChange = (event, { newValue }) => {
        // if (this.validateNumber(newValue)) {
        //     this.props.setCustomerLSINumberInput(newValue);
        // }
        this.props.setCustomerLSINumberInput(newValue);
    };

    onSuggestionsFetchRequested = ({ value }) => {
        this.setState({
            suggestions: this.getSuggestions(value)
        });
    };

    onSuggestionsClearRequested = () => {
        this.setState({
            suggestions: this.getSuggestions("")
        });
    };

    onSuggestionSelected(event, { suggestion, suggestionValue, suggestionIndex, sectionIndex, method }) {
        AnalyticsService.trackEvent({
            eVar9: "b2b|dt|homepage|lsi_number|enter",
            eVar8: "b2b|dt|homepage",
            prop21: suggestionValue,
        })
        this.props.setCustomerLSINumber(suggestionValue);
        this.props.onLSINumberChange(suggestionValue);
    }

    render() {
        const { classes, customerLSINumber, customerSRNumber, customerAccountNumber,
            customerLSIDetails, srDetailsTabApiResponse, srDetailsCategorySpecificData, autoFillLSI,
            accountNumberInput, srNumberInput, lsiNumberInput } = this.props;
        const { suggestions } = this.state;
        const value = lsiNumberInput;
        const inputProps = {
            value: value,
            onChange: this.onChange
        };
        const theme = {
            container: classes.container,
            suggestionsContainerOpen: classes.suggestionsContainerOpen,
            suggestionsList: classes.suggestionsList,
            suggestion: classes.suggestion
        }

        return (
            <AppBar position="static" className={classes.appBar}>
                <h2 className={classes.headerTitle}>
                    DT - B2B
                </h2>
                <div className={classes.rightHeader} id="main-header-right">
                    <form onSubmit={event => event.preventDefault()} className={classes.container + " inline-block vertical-middle"} noValidate autoComplete="off">
                        <TextField
                            label="ENTER ACCOUNT NO."
                            className={classes.textField}
                            value={accountNumberInput}
                            onClick={this.onClickAccountNumber}
                            onChange={this.onChangeAccountNumber}
                            onKeyDown={this.onSubmitAccountNumber}
                            margin="normal"
                            autoFocus={true}
                        />
                    </form>
                    <form onSubmit={event => event.preventDefault()} className={classes.container + " inline-block vertical-middle"} noValidate autoComplete="off">
                        {customerLSIDetails && customerLSIDetails.length > 0 ?
                            (
                                <div >
                                    <Autosuggest
                                        renderInputComponent={this.renderInputComponent}
                                        suggestions={suggestions}
                                        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                                        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                                        getSuggestionValue={this.getSuggestionValue}
                                        renderSuggestion={this.renderSuggestion}
                                        inputProps={inputProps}
                                        theme={theme}
                                        shouldRenderSuggestions={() => true}
                                        renderSuggestionsContainer={options => (
                                            <Paper {...options.containerProps} square>
                                                {options.children}
                                            </Paper>
                                        )}
                                        onSuggestionSelected={this.onSuggestionSelected}

                                    />
                                </div>
                            ) : (
                                <TextField
                                    label="ENTER LSI NO."
                                    className={classes.textField}
                                    value={lsiNumberInput}
                                    onClick = {this.onClickLsiNumber}
                                    onChange={this.onChangeLSINumber}
                                    onKeyDown={this.onSubmitLSINumber}
                                    margin="normal"
                                />
                            )}
                    </form>
                    <form onSubmit={event => event.preventDefault()} className={classes.container + " inline-block vertical-middle"} noValidate autoComplete="off">
                        <TextField
                            label="ENTER SR NUMBER"
                            className={classes.textField}
                            value={srNumberInput}
                            onClick={this.onClickSRNumber}
                            onChange={this.onChangeSRNumber}
                            onKeyDown={this.onSubmitSRNumber}
                            margin="normal"
                        />
                    </form>
                    <div className="inline-block vertical-middle">
                        <ExternalLinks />
                    </div>
                    <div className="inline-block vertical-middle">
                        <Cached onClick={this.refreshHandler} className={classes.refresh + ' color-blue pull-right text-center ml-10'} />
                    </div>
                </div>
                {srDetailsTabApiResponse && srDetailsCategorySpecificData && <PreviousSRHistoryDialog />}
            </AppBar >
        );
    }
}

const mapStateToProps = state => {
    return {
        customerAccountNumber: state.customerInfo.accountNumber,
        customerSRNumber: state.customerInfo.customerSRNumber,
        customerLSINumber: state.customerInfo.customerLSINumber,
        customerLSIDetails: state.customerInfo.lsiDetails,
        isCustomerFound: state.customerInfo.isCustomerFound,
        srDetailsTabApiResponse: state.SRDetailsTabApiInfo.srDetailsTabApiResponse,
        srDetailsCategorySpecificData: state.SRDetailsTabApiInfo.srDetailsCategorySpecificData,
        autoFillLSI: state.customerInfo.autoFillLSI,
        accountNumberInput: state.customerInfo.accountNumberInput,
        lsiNumberInput: state.customerInfo.lsiNumberInput,
        srNumberInput: state.customerInfo.srNumberInput
    };
};

const mapDispatchToProps = {
    setCustomerAccountNumber,
    setCustomerSRNumber,
    setCustomerLSINumber,
    resetCustomerInfoRequest,
    onAccountNumberChange,
    onSRNumberChange,
    onLSINumberChange,
    setCustomerSRNumberInput,
    setCustomerLSINumberInput,
    setCustomerAccountNumberInput,
    resetState,
    enableAllTaggings,
    cardLinksHandler,
    openPreviousInteractionsDialog,
    enableAutoFillLSI,
    disableAutoFillLSI,

}


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(withStyles(styles, { withTheme: true })(Header)));
