import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import EncodeDecodeType from '../actiontype/encode-decode-type';
import CookieManagerService from './cookie.service';
// import { AppConstant } from '../constants/app.constant';

class PredictionService {

    static fetchPredictionRequest() {
        let authDetails = CookieManagerService.getAuthDetails();
        const channel = authDetails.channel || "";
        var url = Config.contextPath + "user/rest/v1.0/home/data/prediction?channel=" + channel;
        return CustomHttpService.get(url, {}, EncodeDecodeType.DEFAULT);
    }

    static initPredictionRequest(reqData) {
        var url = Config.contextPath + "user/rest/v1.0/home/init/prediction";
        return CustomHttpService.post(url, reqData, {}, EncodeDecodeType.DEFAULT);
    }

    static getSubtype(crmCode) {
        var crmCodeArr = crmCode ? crmCode.split('>') : [];
        if (crmCodeArr.length > 4) {
            return crmCodeArr[4];
        }
        return "";
    }
}

export default PredictionService;
