const styles = theme => ({
    appBar: {
        top: 85,
        // marginLeft: '90px',
        // width: 'calc(100% - 90px)',
        backgroundColor: '#fff8f0',
        height: 65,
        boxShadow: 'none',
        borderTop: '1px solid #ddd',
        padding: '10px 20px',
        // padding: '15px 20px 0px 20px',
        // display: 'block'
        display: 'flex',
        flexFlow: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        zIndex: 1
    },
    infoBlock: {
        // display: 'inline-block',
        display: 'flex',
        width: '80%',
    },
    iconBlock: {
        display: 'flex',
        width: '20%',
        justifyContent: 'space-around',
        flexDirection: 'row-reverse'
    },
    tagIcon: {
        top: '2px',
        position: 'relative',
        margin: '0px 5px',
        textAlign: 'center',
        width: 15,
        cursor: 'pointer'
    }
});

export default styles;
