import {
    DialogActionType
} from "../actiontype/dialog.actiontype";
import CommonActionType from '../actiontype/common.actiontype';

const initialState = {
    dialogStatus: false,
    dialogTitle: '',
    callDropDialogStatus: false,
    callDropDialogTitle: '',
    feedbackDialogStatus: false,
    feedbackDialogTitle: '',
    previousInteractionsDialogTitle: '',
    previousInteractionsDialogStatus: false,
    interactionsActiveTab: 'SR',
    callTransferDialogStatus: false,
    callTransferDialogTitle: '',
    arcIssueDialogTitle: '',
    arcIssueDialogStatus: false,
    smsDialogStatus: false,
    smsDialogTitle: '',
    multipleTaggingDialogStatus: false,
    multipleTaggingDialogTitle: '',

};

export default (state = initialState, action) => {
    switch (action.type) {
        case DialogActionType.OPEN_CALLDROP_DIALOG:
            return {
                ...state,
                callDropDialogStatus: action.payload.open,
                callDropDialogTitle: action.payload.title
            };
        case DialogActionType.CLOSE_CALL_DROP_DIALOG:
            return {
                ...state,
                callDropDialogStatus: action.payload.open
            };
        case DialogActionType.CLOSE_SR_HISTORY_DIALOG:
            return {
                ...state,
                previousInteractionsDialogStatus: action.payload.open
            };
        case DialogActionType.OPEN_CALL_TRANSFER_DIALOG:
            return {
                ...state,
                callTransferDialogStatus: action.payload.open,
                callTransferDialogTitle: action.payload.title
            };
        case DialogActionType.OPEN_MULTIPLE_TAGGING_DIALOG:
            return {
                ...state,
                multipleTaggingDialogStatus: action.payload.open,
                multipleTaggingDialogTitle: action.payload.title
            };

        case DialogActionType.CLOSE_CALL_TRANSFER_DIALOG:
            return {
                ...state,
                callTransferDialogStatus: action.payload.open
            };
        case DialogActionType.OPEN_FEEDBACK_DIALOG:
            return {
                ...state,
                feedbackDialogStatus: action.payload.open,
                feedbackDialogTitle: action.payload.title
            };
        case DialogActionType.CLOSE_FEEDBACK_DIALOG:
            return {
                ...state,
                feedbackDialogStatus: action.payload.open
            };
        case DialogActionType.OPEN_ARC_ISSUE_DIALOG:
            return {
                ...state,
                arcIssueDialogStatus: action.payload.open,
                arcIssueDialogTitle: action.payload.title
            };
        case DialogActionType.CLOSE_ARC_ISSUE_DIALOG:
            return {
                ...state,
                arcIssueDialogStatus: action.payload.open
            };
        case DialogActionType.CLOSE_MULTIPLE_TAGGING_DIALOG:
            return {
                ...state,
                multipleTaggingDialogStatus: action.payload.open
            };

        case DialogActionType.OPEN_SR_HISTORY_DIALOG:
            return {
                ...state,
                dialogStatus: action.payload.open,
                dialogTitle: action.payload.title
            };
        case DialogActionType.OPEN_PREVIOUS_INTERACTIONS_DIALOG:
            return {
                ...state,
                previousInteractionsDialogStatus: action.payload.open,
                previousInteractionsDialogTitle: action.payload.title,
                previousInteractionsActiveTab: action.payload.interactionsActiveTab
            };
        case DialogActionType.OPEN_SMS_DIALOG:
            return {
                ...state,
                smsDialogStatus: action.payload.open,
                smsDialogTitle: action.payload.title
            };
        case DialogActionType.CLOSE_SMS_DIALOG:
            return {
                ...state,
                smsDialogStatus: action.payload.open
            };
        case DialogActionType.CLOSE_DIALOG:
            return {
                ...state,
                dialogStatus: action.payload.open
            };
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            };

        default:
            return state;
    }
};
