import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import EncodeDecodeType from '../actiontype/encode-decode-type';

class CallDropDialogService {

    //submit call drop in case of scenario and none scenario
    static submitCallDropApi(callDropObj) {
        const config = {
            headers: {
            }
        };
        var url = Config.contextPath + "user/rest/v1.0/tag/postpaid/siebel/callDrop";
        return CustomHttpService.post(url, callDropObj, config, EncodeDecodeType.DEFAULT);
    }

    //fetch call drop dialog data
    static fetchCallDropApi() {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const lob = authDetails && authDetails['lob'] ? authDetails['lob'] : null;
        var url = Config.contextPath + "user/rest/v1.0/tag/postpaid/siebel/droppedCall?agentId=" + agentId + "&lob=" + lob;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    static handleUpdatedSystemChecksList(systemCheckList) {
        let updatedSystemCheckList = {};
        if (systemCheckList && systemCheckList.length) {
            for (var i = 0; i < systemCheckList.length; i++) {
                updatedSystemCheckList[systemCheckList[i].key] = { 'stepId': '1', 'displayText': '' + systemCheckList[i].description, 'markedChoiceColor': '' + systemCheckList[i].markedChoiceColor };
            }

            return updatedSystemCheckList;
        }
        else {
            return null;
        }



    }
    static handleUpdatedSystemInfoList(sysInfoData) {
        let updatedSystemInfoList = {};
        if (sysInfoData && sysInfoData.length) {
            for (var i = 0; i < sysInfoData.length; i++) {
                updatedSystemInfoList[sysInfoData[i].key] = "" + sysInfoData[i].description;
            }
            return updatedSystemInfoList;
        }
        else {
            return null;
        }


    }
}

export default CallDropDialogService;
